public void lookup(FormControl _formControl, str _filterStr)
{
    #ISOCountryRegionCodes

    LogisticsAddressState::lookupStateId(_formControl, SysCountryRegionCode::getCountryRegionIdByIsoCode(#isoBR));
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.setLookupButton();
    }
    return ret;
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret = TaxationCodeTable_BR::resolveReferenceByTaxType(_formReferenceControl, TaxType_BR::ICMSST);

    if (!ret)
    {
        ret = super(_formReferenceControl);
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return TaxationCodeTable_BR::lookupReferenceByTaxType(_formReferenceControl, TaxType_BR::ICMSST);
}
