[Form]
public class FBPresumedEffectiveTaxesRS_BR extends FormRun
{
    FBTaxAssessment_BR fbTaxAssessment;
    FBBookingPeriod_BR fbBookingPeriod;
    FBParametersPerState_BR fiscalBooksParametersPerState;
    FBSubAssessmentCalculatorICMSST_BR subAssessmentCalculator;

    PresumedTaxAlgorithm_BR presumedTaxAlgorithm;

}

public void init()
{
    if (!element.args() || !element.args().record() || element.args().dataset() != tableNum(FBTaxAssessment_BR))
    {
        throw error(error::missingFormActiveBuffer(this.name(), tableStr(FBTaxAssessment_BR)));
    }

    fbTaxAssessment = element.args().record();
    fbBookingPeriod = fbTaxAssessment.bookingPeriod();
    fiscalBooksParametersPerState = FBParametersPerState_BR::find(fbBookingPeriod.fiscalEstablishment().postalAddress());

    presumedTaxAlgorithm = fiscalBooksParametersPerState.PresumedTaxAlgorithmSPED;

    super();

    subAssessmentCalculator = FBSubAssessmentCalculatorICMSST_BR::construct();
    subAssessmentCalculator.parmFBBookingPeriod(fbBookingPeriod);
    subAssessmentCalculator.parmIsRetail(fiscalBooksParametersPerState.IsRetailCompany);
    subAssessmentCalculator.parmSubTaxAssessment(fiscalBooksParametersPerState.FBSubTaxAssessment_BR);

    this.recalculateTotals();

    element.updateDesign();

    this.calculationHasToBeRunValidation();
}

private void calculationHasToBeRunValidation()
{
    FBBookingPeriodFiscalDocument_BR fbBookingPeriodFiscalDocument;
    FiscalDocument_BR fiscalDocument;
    FiscalDocumentLine_BR fiscalDocumentLine;
    FiscalDocumentTaxTrans_BR fiscalDocumentTaxTrans;
    FiscalDocumentTaxTransPresumed_BR fiscalDocumentTaxTransPresumed;

    select firstOnly RecId from fiscalDocumentTaxTransPresumed
        where fiscalDocumentTaxTransPresumed.SPEDOriginalTaxTrans != 0
        exists join fiscalDocumentTaxTrans
            where fiscalDocumentTaxTrans.RecId == fiscalDocumentTaxTransPresumed.FiscalDocumentTaxTrans_BR
        exists join fiscalDocumentLine
            where fiscalDocumentLine.RecId == fiscalDocumentTaxTrans.FiscalDocumentLine
        exists join fiscalDocument
            where fiscalDocument.RecId == fiscalDocumentLine.FiscalDocument
                && fiscalDocument.Direction == FiscalDocDirection_BR::Outgoing
        exists join fbBookingPeriodFiscalDocument
            where fbBookingPeriodFiscalDocument.FiscalDocument_BR == fiscalDocument.RecId
                && fbBookingPeriodFiscalDocument.FBBookingPeriod_BR == fbBookingPeriod.RecId;

    if (!fiscalDocumentTaxTransPresumed.RecId)
    {
        warning("@FiscalBooks_SpedFiscal:ValidatePresumedTaxesNotCalculated");
    }
}

/// <summary>
/// Shows the amount of credit balance for ICMS-ST sub tax assessment.
/// </summary>
/// <returns>
/// The amount in master currency.
/// </returns>
/// <remarks>
/// Only for RS state currently.
/// </remarks>
public display AmountMST creditBalance()
{
    return subAssessmentCalculator && subAssessmentCalculator.getBalance() <= 0 ? -subAssessmentCalculator.getBalance() : 0;
}

/// <summary>
/// Shows the amount of credits from incoming fiscal documents for ICMS-ST sub tax assessment.
/// </summary>
/// <returns>
/// The amount in master currency.
/// </returns>
/// <remarks>
/// Only for RS state currently.
/// </remarks>
public display AmountMST creditIncoming()
{
    return subAssessmentCalculator ? subAssessmentCalculator.getCreditAdjustments() : 0;
}

