/// <summary>
/// Recalculates totals for current ICMS-ST sub tax assessment.
/// </summary>
/// <remarks>
/// Only for RS state currently.
/// </remarks>
public void recalculateTotals()
{
    RunbaseProgress progress;

    if (subAssessmentCalculator)
    {
        progress = RunbaseProgress::construct();
        progress.setFormRun(element);
        subAssessmentCalculator.parmProgress(progress);
        subAssessmentCalculator.calculate();
        progress.kill();
        subAssessmentCalculator.parmProgress(null);
    }
}

