/// Updates form design.
/// </summary>
public void updateDesign()
{
    IncTabPage.visible(!fiscalBooksParametersPerState.IsRetailCompany);
    IncRetailTabPage.visible(fiscalBooksParametersPerState.IsRetailCompany);
    CreditReversalTabPage.visible(fiscalBooksParametersPerState.IsRetailCompany);

    FiscalDocumentPurchOut_FiscalDocumentNumber.visible(!fiscalBooksParametersPerState.IsRetailCompany);
    FiscalDocumentTaxTransPresumedOut_SPEDPresumedTaxBaseAmount.visible(!fiscalBooksParametersPerState.IsRetailCompany);
    FiscalDocumentTaxTransPresumedOut_SPEDPresumedTaxPercentage.visible(!fiscalBooksParametersPerState.IsRetailCompany);
    FiscalDocumentTaxTransPresumedOut_SPEDPresumedTaxAmount.visible(!fiscalBooksParametersPerState.IsRetailCompany);
}

public void init()
{
    super();

    findOrCreateRange_W(
        this.queryBuildDataSource(),
        fieldNum(FiscalDocument_BR, Direction),
        queryValue(FiscalDocDirection_BR::Outgoing))
        .status(RangeStatus::Locked);

    findOrCreateRange_W(
        this.queryBuildDataSource(),
        fieldNum(FiscalDocument_BR, Status),
        queryValue(FiscalDocumentStatus_BR::Approved))
        .status(RangeStatus::Locked);

    findOrCreateRange_W(
        this.queryBuildDataSource(),
        fieldNum(FiscalDocument_BR, FinalUser),
        queryValue(NoYes::Yes))
        .status(RangeStatus::Locked);

    if (fiscalBooksParametersPerState.IsRetailCompany)
    {
        findOrCreateRange_W(
            this.queryBuildDataSource(),
            fieldNum(FiscalDocument_BR, RefTableId),
            strFmt('%1,%2,%3', SysQuery::valueNot(tableNum(VendInvoiceJour)), SysQuery::valueNot(tableNum(VendPackingSlipJour)), SysQuery::valueNot(tableNum(PurchComplementaryInvoiceHeader))))
            .status(RangeStatus::Locked);
    }
}

