public void clicked()
{
    super();

    FiscalDocumentPresumedTaxCalc_BR fiscalDocumentPresumedTaxCalc = FiscalDocumentPresumedTaxCalc_BR::initPresumedTaxCalc(presumedTaxAlgorithm);
    FiscalEstablishmentId_BR fiscalEstablishmentId = fbBookingPeriod.FiscalEstablishmentId;
    fiscalDocumentPresumedTaxCalc.parmFiscalEstablishmentId(fiscalEstablishmentId);
    fiscalDocumentPresumedTaxCalc.parmFBBookingPeriod(fbBookingPeriod);
    fiscalDocumentPresumedTaxCalc.parmFiscalDocumentDate(endmth(mkDate(1, fbBookingPeriod.PeriodMonth, fbBookingPeriod.PeriodYear)));
    fiscalDocumentPresumedTaxCalc.init();
    fiscalDocumentPresumedTaxCalc.calculatePresumedTaxForSPED();

    element.recalculateTotals();
    FiscalDocumentOut_ds.executeQuery();
    FBBookingPeriodPresumedIncDocuments_ds.executeQuery();
    FiscalDocumentInRetail_ds.executeQuery();
    FiscalDocumentOutCR_ds.executeQuery();
}

public void clicked()
{
    FBSubAssessmentAdjustmentCreator_BR adjCreator = new FBSubAssessmentAdjustmentCreator_BR();

    adjCreator.parmFBBookingPeriod(fbBookingPeriod);
    adjCreator.parmSubAssessmentCalculatorICMSST(subAssessmentCalculator);
    adjCreator.createAdjustment();
}
