void clicked()
{
    FiscalDocumentPresumedTaxCalc_BR       fiscalDocumentPresumedTaxCalc;
    FiscalEstablishmentId_BR               fiscalEstablishmentId;

    super();

    fiscalDocumentPresumedTaxCalc = FiscalDocumentPresumedTaxCalc_BR::initPresumedTaxCalc(presumedTaxAlgorithm);
    fiscalEstablishmentId = fbBookingPeriod.FiscalEstablishmentId;
    fiscalDocumentPresumedTaxCalc.parmFiscalEstablishmentId(fiscalEstablishmentId);
    fiscalDocumentPresumedTaxCalc.parmFBBookingPeriod(fbBookingPeriod);
    fiscalDocumentPresumedTaxCalc.parmFiscalDocumentDate(endmth(mkDate(1, fbBookingPeriod.PeriodMonth, fbBookingPeriod.PeriodYear)));
    fiscalDocumentPresumedTaxCalc.parmState(FiscalBooksConstants_BR::StateSC);
    fiscalDocumentPresumedTaxCalc.init();
    fiscalDocumentPresumedTaxCalc.calculatePresumedTaxForSPED();

    fiscalDocumentOut_ds.executeQuery();
    fbBookingPeriodPresumedIncDocuments_ds.executeQuery();
}
