Method.Source:
[Form]
public class FBTaxAssessmentAdjustment_BR extends FormRun
{

    FBTaxAssessment_BR  fbTaxAssessment;
}

public void init()
{
    super();

    if (element.args().dataset() == tableNum(FBTaxAssessment_BR)
        && element.args().record().RecId)
    {
        fbTaxAssessment = element.args().record();
    }
    else
    {
        throw error("@SYS22996");
    }

    element.addFilter();
    element.updateDesign();
}

public void updateDesign()
{
    if (fbTaxAssessment.TaxType == TaxType_BR::ICMS)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, IsTaxDifference)).visible(true);
    }
    else
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, IsTaxDifference)).visible(false);
    }

    if (fbTaxAssessment.TaxType == TaxType_BR::PIS || fbTaxAssessment.TaxType == TaxType_BR::COFINS)
    {
        this.updateDesignTaxTypePisCofins();
    }
    else
    {
        OtherDetails.visible(false);
    }

    if (fbTaxAssessment.TaxType == TaxType_BR::IPI)
    {
        this.updateDesignTaxTypeIpi();
    }
    else
    {
        SourceType.visible(false);
    }        

    if (BrazilParameters::isFBLedgerIntegrationActivated() && !fbTaxAssessment.isICMSTaxAssessment() && !fbTaxAssessment.isPISCOFINSTaxAssessment())
    {
        FBTaxAssessmentAdjustment_BR_ds.allowCreate(false);
        FBTaxAssessmentAdjustment_BR_ds.allowDelete(false);
    }

    LedgerTransVoucher.visible(BrazilParameters::isFBLedgerIntegrationActivated());
    CancelLedgerTransVoucher.visible(BrazilParameters::isFBLedgerIntegrationActivated());

    if (fbTaxAssessment.isINSSCPRBTaxAssessment())
    {
        this.updateDesignTaxTypeInsscprb();
    }
}

private void updateDesignTaxTypePisCofins()
{
    OtherDetails.visible(true);

    FBGeneralAdjustmentCodePISCOFINS_BR generalAdjustmentCode = FBGeneralAdjustmentCode_BR::find(FBTaxAssessmentAdjustment_BR.FBGeneralAdjustmentCode_BR) as FBGeneralAdjustmentCodePISCOFINS_BR;

    boolean isReceiptNumberEnabled = generalAdjustmentCode.isBaseAdjustmentType();
    boolean isProcessNumEnabled = isReceiptNumberEnabled;
    boolean isCreatePaymentEnabled = !isReceiptNumberEnabled;
    boolean isCreditBaseSourceEnabled = !isReceiptNumberEnabled;
    boolean isTaxAmountEnabled = !isReceiptNumberEnabled;
    boolean isTransDateEnabled = isReceiptNumberEnabled;

    if (generalAdjustmentCode)
    {
        boolean isCreditTypeEnabled                           = FBTaxAssessmentAdjustment_BR::isCreditTypeMandatory(generalAdjustmentCode.AdjustmentType);
        boolean isTaxationCodeEnabled                         = FBTaxAssessmentAdjustment_BR::isTaxationCodeMandatory(generalAdjustmentCode.AdjustmentType);
        boolean isTaxBaseAmountEnabled                        = FBTaxAssessmentAdjustment_BR::isTaxBaseAmountMandatory(generalAdjustmentCode.AdjustmentType);
        boolean isTaxValueEnabled                             = FBTaxAssessmentAdjustment_BR::isTaxValueMandatory(generalAdjustmentCode.AdjustmentType);
        boolean isCnpjEnabled                                 = FBTaxAssessmentAdjustment_BR::isCnpjMandatory(generalAdjustmentCode.AdjustmentType);
        boolean isContribAssessmentPeriodEnabled              = FBTaxAssessmentAdjustment_BR::isAssessmentPeriodMandatory(generalAdjustmentCode.AdjustmentType);
        boolean isContribTransferredCreditPercentageEnabled   = FBTaxAssessmentAdjustment_BR::isTransferredCreditPercentageMandatory(generalAdjustmentCode.AdjustmentType);
        isProcessNumEnabled = isReceiptNumberEnabled || (generalAdjustmentCode.AdjustmentType == FBGeneralAdjustmentTypePISCOFINS_BR::CreditIncrease
            || generalAdjustmentCode.AdjustmentType == FBGeneralAdjustmentTypePISCOFINS_BR::CreditDecrease);

        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TransDate)).enabled(!BrazilParameters::isFBLedgerIntegrationActivated() || isTransDateEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TransDate)).allowEdit(!BrazilParameters::isFBLedgerIntegrationActivated() || isTransDateEnabled);

        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, FBContribCreditType_BR)).enabled(isCreditTypeEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxationCode)).enabled(isTaxationCodeEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxBaseAmount)).enabled(isTaxBaseAmountEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxValue)).enabled(isTaxValueEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, DeductionCNPJ)).enabled(isCnpjEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ContribAssessmentPeriod)).enabled(isContribAssessmentPeriodEnabled);
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ContribTransferredCreditPercentage)).enabled(isContribTransferredCreditPercentageEnabled);

        if (!isCreditTypeEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, FBContribCreditType_BR)).setValue(0);
        }

        if (!isTaxationCodeEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxationCode)).setValue("");
        }

        if (!isTaxBaseAmountEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxBaseAmount)).setValue("");
        }

        if (!isTaxValueEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxValue)).setValue(0);
        }

        if (!isCnpjEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, DeductionCNPJ)).setValue("");
        }

        if (!isContribAssessmentPeriodEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ContribAssessmentPeriod)).setValue(dateNull());
        }

        if (!isContribTransferredCreditPercentageEnabled)
        {
            FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ContribTransferredCreditPercentage)).setValue(0);
        }
    }

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAdjustmentProcessNum)).enabled(isProcessNumEnabled);

    if (!isProcessNumEnabled)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAdjustmentProcessNum)).setValue("");
    }

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ReceiptNumber)).enabled(isReceiptNumberEnabled);

    if (!isReceiptNumberEnabled)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ReceiptNumber)).setValue("");
    }

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, CreatePayment)).enabled(isCreatePaymentEnabled);

    if (!isCreatePaymentEnabled)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, CreatePayment)).setValue(NoYes::No);
    }

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ContribCreditBaseSource)).enabled(isCreditBaseSourceEnabled);

    if (!isCreditBaseSourceEnabled)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, ContribCreditBaseSource)).setValue(0);
    }

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAmount)).enabled(isTaxAmountEnabled);

    if (!isTaxAmountEnabled)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAmount)).setValue(0);
    }

    FBTaxAssessmentAdjustment_BR_ds.rereadReferenceDataSources();
}

private void updateDesignTaxTypeIpi()
{
    SourceType.visible(true);

    boolean isProcessNumEnabled = FBGeneralAdjustmentCodeIPI_BR::isProcessNumAllowed(FBTaxAssessmentAdjustment_BR.TaxAdjustmentSourceType);        

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAdjustmentProcessNum)).enabled(isProcessNumEnabled);
    if (!isProcessNumEnabled)
    {
        FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAdjustmentProcessNum)).setValue("");
    }
}

private void updateDesignTaxTypeInsscprb()
{
    FBTaxAssessmentAdjustment_BR_ds.allowCreate(!fbTaxAssessment.isClosed());
    FBTaxAssessmentAdjustment_BR_ds.allowDelete(!fbTaxAssessment.isClosed());

    LedgerPosting.visible(false);
    TaxPayment.visible(false);
    CancelInformation.visible(false);
    LedgerTransVoucher.visible(false);
    CancelLedgerTransVoucher.visible(false);
    AdjustmentDetailsINSSCPRB.visible(true);

    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, FBEconomicActivityCode_BR)).mandatory(true);
    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TaxAmount)).mandatory(true);
    FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, TransDate)).mandatory(true);
}

public boolean canClose()
{
    boolean ret = super();

    if (ret && fbTaxAssessment.TaxType == TaxType_BR::INSSCPRB)
    {
        FBTaxAssessment_BR fbTaxAssessment_br = FBTaxAssessment_BR::find(FBTaxAssessmentAdjustment_BR_ds.object(fieldNum(FBTaxAssessmentAdjustment_BR, fbTaxAssessment_BR)).getValue());
        if(fbTaxAssessment_br && !fbTaxAssessment_br.isClosed())
        {
            FBTaxAssessmentAdjustment_BR_ds.write();
        }

        new FBTaxAssessmentDetailCreatorINSSCPRB_BR(fbTaxAssessment).updateAssessmentFromAdjustments();
    }

    return ret;
}

public int active()
{
    SecurityRights curUserRights;
    boolean hasUserRights;

    int ret;

    ret = super();

    curUserRights = SecurityRights::newUser(curUserId());
    hasUserRights = curUserRights.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(LedgerTransVoucher1));
    LedgerTransVoucher.enabled(hasUserRights && FBTaxAssessmentAdjustment_BR.VoucherDataArea != '');
    CancelLedgerTransVoucher.enabled(hasUserRights && FBTaxAssessmentAdjustment_BR.VoucherDataArea != '');

    FBTaxAssessmentAdjustment_BR_ds.allowEdit(FBTaxAssessmentAdjustment_BR.Voucher == "");
    FBTaxAssessmentAdjustment_BR_ds.allowDelete(FBTaxAssessmentAdjustment_BR.Voucher  == "");

    element.updateDesign();

    return ret;
}

public void initValue()
{
    super();

    FBTaxAssessmentAdjustment_BR.FBTaxAssessment_BR = fbTaxAssessment.RecId;
    FBTaxAssessmentAdjustment_BR.TaxType_BR = fbTaxAssessment.TaxType;
}

public boolean validate()
{
    boolean                                 ret;
    FBGeneralAdjustmentCode_BR              generalAdjustmentCode;
    FBGeneralAdjustmentCodeICMS_BR          generalAdjustmentCodeICMS;

    ret = super();

    generalAdjustmentCode = FBGeneralAdjustmentCode_BR::find(this.getValue());

    if (BrazilParameters::isFBLedgerIntegrationActivated() && generalAdjustmentCode.isICMSTaxType())
    {
        generalAdjustmentCodeICMS = generalAdjustmentCode;

        // Adjustment classification must be 'information'.
        if (fbTaxAssessment.TaxType == TaxType_BR::ICMSDiff &&
            generalAdjustmentCodeICMS.Classification != FBGeneralClassificationICMS_BR::Information)
        {
            ret = ret && checkFailed(strFmt("@Brazil:FBIcmsDifAdjustmentClassificationIsInvalid", FBGeneralClassificationICMS_BR::Information));
        }
    }

    return ret;
}

public void jumpRef()
{
    //Call the respective form
    FBTaxAssessmentAdjustment_BR.generalAdjustmentCode().jumpRef(fbTaxAssessment.TaxType);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    container icmsClassifications;

    if (BrazilParameters::isFBLedgerIntegrationActivated())
    {
        icmsClassifications = [FBGeneralClassificationICMS_BR::Information];
    }

    return FBGeneralAdjustmentCode_BR::lookupReferenceAdjustmentCode(_formReferenceControl, fbTaxAssessment.TaxType, fbTaxAssessment.State, icmsClassifications, FBTaxAssessmentAdjustmentType_BR::General);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup;
    Query                   query                       = new Query();
    QueryBuildDataSource    queryBuildDataSource        = query.addDataSource(tableNum(TaxationCodeTable_BR));
    QueryBuildRange         queryBuildRangeTaxType      = queryBuildDataSource.addRange(fieldNum(TaxationCodeTable_BR, TaxType));

    sysTableLookup = SysTableLookup::newParameters(tableNum(TaxationCodeTable_BR), _formControl);

    queryBuildRangeTaxType.value(queryValue(fbTaxAssessment.TaxType));

    queryBuildDataSource.addRange(fieldNum(TaxationCodeTable_BR, ToDate)).value(queryRange(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()), SysQuery::valueUnlimited()) + ',' + SysQuery::valueEmptyString());
    queryBuildDataSource.addRange(fieldNum(TaxationCodeTable_BR, FromDate)).value(queryRange(SysQuery::valueUnlimited(), DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone())) + ',' + SysQuery::valueEmptyString());

    sysTableLookup.addLookupfield(fieldNum(TaxationCodeTable_BR, TaxationCode));
    sysTableLookup.addLookupfield(fieldNum(TaxationCodeTable_BR, Description));
    sysTableLookup.addLookupfield(fieldNum(TaxationCodeTable_BR, FiscalValue));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

void clicked()
{
    Args args;
    MenuFunction menuFunction;
    TaxTrans taxTrans;

    menuFunction = new menuFunction(menuitemDisplayStr(ledgerTransVoucher), MenuItemType::Display);

    changeCompany (FBTaxAssessmentAdjustment_BR.VoucherDataArea)
    {
        select firstOnly taxTrans
            where taxTrans.Voucher     == FBTaxAssessmentAdjustment_BR.Voucher
                && taxTrans.TransDate   == FBTaxAssessmentAdjustment_BR.TransDate;

        args = new args(this);
        args.record(taxTrans);
        menuFunction.run(args);
    }
}

void clicked()
{
    Args args;
    MenuFunction menuFunction;
    TaxTrans taxTrans;

    menuFunction = new menuFunction(menuitemDisplayStr(ledgerTransVoucher), MenuItemType::Display);

    changeCompany (FBTaxAssessmentAdjustment_BR.VoucherDataArea)
    {
        select firstOnly taxTrans
            where taxTrans.Voucher     == FBTaxAssessmentAdjustment_BR.CancelVoucher
                && taxTrans.TransDate   == FBTaxAssessmentAdjustment_BR.CancelTransDate;

        args = new args(this);
        args.record(taxTrans);
        menuFunction.run(args);
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.updateDesign();

    return ret;
}
