[Form]
public class FBTaxAssessmentPaymentOtherDebits_BR extends FormRun
{
    FBTaxAssessment_BR          taxAssessment;
    FBTaxAssessmentPayment_BR   taxAssessmentPayment;

}

public List getSelectedFiscalDocumentAdjustments()
{
    List selectedRecords;

    FBFiscalDocumentAdjustment_BR fiscalDocumentAdjustment;

    selectedRecords = new List(Types::Record);

    fiscalDocumentAdjustment = fbFiscalDocumentAdjustment_BR_ds.getFirst(true);
    while (fiscalDocumentAdjustment)
    {
        selectedRecords.addEnd(fiscalDocumentAdjustment);

        fiscalDocumentAdjustment = fbFiscalDocumentAdjustment_BR_ds.getnext();
    }

    return selectedRecords;
}

public List getSelectedTaxAssessmentAdjustments()
{
    List selectedRecords;

    FBTaxAssessmentAdjustment_BR taxAdjustment;

    selectedRecords = new List(Types::Record);

    taxAdjustment = fbTaxAssessmentAdjustment_BR_ds.getFirst(true);
    while (taxAdjustment)
    {
        selectedRecords.addEnd(taxadjustment);

        taxadjustment = fbtaxassessmentadjustment_br_ds.getnext();
    }

    return selectedRecords;
}

public void init()
{
    super();

    if (element.args())
    {
        if (element.args().dataset() == tableNum(FBTaxAssessment_BR))
        {
            taxAssessment = element.args().record();
        }
        else if (element.args().dataset() == tableNum(FBTaxAssessmentPayment_BR))
        {
            taxAssessmentPayment = element.args().record();
            taxAssessment = FBTaxAssessment_BR::find(taxAssessmentPayment.FBTaxAssessment_BR);
        }

        FiscalDocumentAdjustmentTab.visible(taxAssessment.allowAdjustmentByFiscalDocument());
        GeneralAdjustmentTab.activatePage();

        if (taxAssessmentPayment)
        {
            Ok.text("@SYS23285");
        }
    }
}

public void linkActive()
{
    Query                           query;
    FBTaxAssessmentAdjustment_BR    taxAssessmentAdjustment;
    FBTaxTrans_BR                   taxTrans;
    QueryBuildDataSource            taxTransTaxPaymentDS;

    query = FBTaxAssessmentAdjustment_BR_ds.query();
    query.allowCrossCompany();

    query.dataSourceTable(tableNum(FBTaxAssessmentAdjustment_BR))
         .addRange(fieldNum(FBTaxAssessmentAdjustment_BR, FBTaxAssessment_BR))
         .value(queryValue(taxAssessment.RecId));

    query.dataSourceTable(tableNum(FBTaxAssessmentAdjustment_BR))
         .addRange(fieldNum(FBTaxAssessmentAdjustment_BR, CreatePayment))
         .value(queryValue(NoYes::Yes));

    query.dataSourceTable(tableNum(FBTaxAssessmentAdjustment_BR))
         .addRange(fieldNum(FBTaxAssessmentAdjustment_BR, IsCancelled))
         .value(queryValue(NoYes::No));

    if (taxAssessmentPayment)
    {
        taxTransTaxPaymentDS = query.dataSourceTable(tableNum(FBTaxTrans_BR))
                                    .addDataSource(tableNum(FBTaxTransTaxAssessmentPayment_BR));
        taxTransTaxPaymentDS.relations(true);
        taxTransTaxPaymentDS.joinMode(JoinMode::NoExistsJoin);
        taxTransTaxPaymentDS.addRange(fieldNum(FBTaxTransTaxAssessmentPayment_BR, FBTaxAssessmentPayment_BR))
                            .value(SysQuery::valueNot(taxAssessmentPayment.RecId));
    }
    else
    {
        taxTransTaxPaymentDS = query.dataSourceTable(tableNum(FBTaxTrans_BR))
                                    .addDataSource(tableNum(FBTaxTransTaxAssessmentPayment_BR));
        taxTransTaxPaymentDS.relations(true);
        taxTransTaxPaymentDS.joinMode(JoinMode::NoExistsJoin);
    }

    FBTaxAssessmentAdjustment_BR_ds.executeQuery();

    if (taxAssessmentPayment)
    {
        taxAssessmentAdjustment = FBTaxAssessmentAdjustment_BR_ds.getFirst();
        taxTrans = FBTaxTrans_BR_ds.getFirst();
        while (taxAssessmentAdjustment)
        {
            if (FBTaxTransTaxAssessmentPayment_BR::exists(taxTrans.RecId, taxAssessmentPayment.RecId))
            {
                FBTaxAssessmentAdjustment_BR_ds.markRecord(taxAssessmentAdjustment, 1);
            }

            taxAssessmentAdjustment = FBTaxAssessmentAdjustment_BR_ds.getNext();
            taxTrans = FBTaxTrans_BR_ds.getNext();
        }
    }
}

public void linkActive()
{
    Query                           query;
    FBFiscalDocumentAdjustment_BR   fiscalDocumentAdjustment;
    FBTaxTrans_BR                   taxTrans;
    QueryBuildDataSource            taxTransTaxPaymentDS;

    query = FBFiscalDocumentAdjustment_BR_ds.query();
    query.allowCrossCompany(true);

    query.dataSourceTable(tableNum(FBFiscalDocumentAdjustment_BR))
         .addRange(fieldNum(FBFiscalDocumentAdjustment_BR, FBTaxAssessment_BR))
