         .value(queryValue(NoYes::No));

    if (taxAssessmentPayment)
    {
        taxTransTaxPaymentDS = query.dataSourceTable(tableNum(FBTaxTrans_BR))
                                    .addDataSource(tableNum(FBTaxTransTaxAssessmentPayment_BR));
        taxTransTaxPaymentDS.relations(true);
        taxTransTaxPaymentDS.joinMode(JoinMode::NoExistsJoin);
        taxTransTaxPaymentDS.addRange(fieldNum(FBTaxTransTaxAssessmentPayment_BR, FBTaxAssessmentPayment_BR))
