void clicked()
{
    FBTaxAssessmentPaymentCreator_BR creator;
    super();

    ttsBegin;

    if (taxAssessmentPayment)
    {
        creator = FBTaxAssessmentPaymentCreator_BR::updateFromAdjustment(
                                                        taxAssessment,
                                                        taxAssessmentPayment,
                                                        element.getSelectedTaxAssessmentAdjustments(),
                                                        element.getSelectedFiscalDocumentAdjustments());
    }
    else
    {
        creator = FBTaxAssessmentPaymentCreator_BR::createFromAdjustment(
                                                        taxAssessment,
                                                        DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()),
                                                        element.getSelectedTaxAssessmentAdjustments(),
                                                        element.getSelectedFiscalDocumentAdjustments());
    }

    creator.run();

    ttsCommit;
}
