Method.Source:
[Form]
public class FBTaxAssessmentPayment_BR extends FormRun
{
    FBTaxAssessment_BR taxAssessment;
    FBBookingPeriod_BR bookingPeriod;

    boolean             mustCreatePaymentByFiscalDocument;
}

public void init()
{
    FiscalEstablishment_BR fiscalEstablishment;

    super();

    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR10008, funcName());

    if (element.args() && element.args().dataset() == tableNum(FBTaxAssessment_BR))
    {
        taxAssessment = element.args().record();
        bookingPeriod = FBBookingPeriod_BR::find(taxAssessment.FBBookingPeriod_BR);

        switch (taxAssessment.TaxType)
        {
            case TaxType_BR::ICMS :
                FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, ICMSObligationCode)).visible(true);
                OtherDebits.visible(true);
                break;

            case TaxType_BR::ICMSST :
                FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, ICMSObligationCode)).visible(true);
                OtherDebits.visible(true);
                PaymentDataGroup.visible(true);
                break;

            case TaxType_BR::ICMSDiff:
                FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, ICMSObligationCode)).visible(true);
                OtherDebits.visible(true);
                PostPayment.multiSelect(true);
                PostPayment.needsRecord(true);

                mustCreatePaymentByFiscalDocument = taxAssessment.mustCreatePaymentsByFiscalDocument();
                break;

            case TaxType_BR::IPI :
                FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, ICMSObligationCode)).visible(false);
                OtherDebits.visible(true);
                break;

            case TaxType_BR::PIS:
            case TaxType_BR::COFINS:
                FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, ICMSObligationCode)).visible(false);
                OtherDebits.visible(true);
                break;

            default :
                FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, ICMSObligationCode)).visible(false);
                OtherDebits.visible(false);
                PaymentDataGroup.visible(false);
        }

        fiscalEstablishment = bookingPeriod.fiscalEstablishment();

        HeaderTitle.text(strFmt('%1 : %2', fiscalEstablishment.Name, fiscalEstablishment.CNPJCPFNum_BR));
    }

    this.updateDesign();
}

private void updateDesign()
{
    FBTaxTrans.visible(BrazilParameters::isFBLedgerIntegrationActivated());
    LedgerTransVoucher.visible(BrazilParameters::isFBLedgerIntegrationActivated());
    VendTransButton.visible(BrazilParameters::isFBLedgerIntegrationActivated());
    PostPayment.visible(BrazilParameters::isFBLedgerIntegrationActivated());
    FBTaxAssessmentPayment_BR_ContribAssessmentRegimen.visible(taxAssessment.TaxType == TaxType_BR::PIS || taxAssessment.TaxType == TaxType_BR::COFINS);
}

private void updateDesign()
{
    FBTaxAssessmentPayment_BR_ds.allowDelete(FBTaxAssessmentPayment_BR.Voucher  == "");
    this.allowEditAllFields(FBTaxAssessmentPayment_BR.Voucher == "");

    switch (taxAssessment.TaxType)
    {
        case TaxType_BR::ICMSST :
            FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, GNREProtocolNumber)).allowEdit(true);
            FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, PaymentAuthenticationNumber)).allowEdit(true);
            FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, PaymentBankAgency)).allowEdit(true);
            FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, PaymentBankCode)).allowEdit(true);
            FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, PaymentDate)).allowEdit(true);
            break;
    }

    EditPaymentProposal.enabled(FBTaxAssessmentPayment_BR.PaymentType == FBTaxAssessmentPaymentType_BR::OtherDebits &&
                                FBTaxAssessmentPayment_BR.Voucher == "");

    PostPayment.enabled(FBTaxAssessmentPayment_BR.Voucher  == "");
    FBTaxTrans.enabled(FBTaxAssessmentPayment_BR.Reversed == false);

    FBTaxAssessmentPayment_BR_ds.object(fieldNum(FBTaxAssessmentPayment_BR, RevenueCode)).allowEdit(FBTaxAssessmentPayment_BR.PaymentType != FBTaxAssessmentPaymentType_BR::OtherDebits);
}

public void allowEditAllFields(boolean allowEdit)
{
    DictTable dictTable;
    DictField dictField;
    int i;

    dictTable = new dictTable(tableNum(FBTaxAssessmentPayment_BR));

    for (i = 1; i < dictTable.fieldCnt(); i++)
    {
        dictField = new DictField(tableNum(FBTaxAssessmentPayment_BR), dictTable.fieldCnt2Id(i));

        if (!dictField.isSystem())
        {
            FBTaxAssessmentPayment_BR_ds.object(dictField.id()).allowEdit(allowEdit);
        }
    }
}

public int active()
{
    SecurityRights curUserRights;
    boolean hasUserRights;

    int ret;

    ret = super();

    curUserRights = SecurityRights::newUser(curUserId());
    hasUserRights = curUserRights.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(LedgerTransVoucher1));
    LedgerTransVoucher.enabled((hasUserRights && FBTaxAssessmentPayment_BR.TaxAuthorityDataArea != '' && FBTaxAssessmentPayment_BR.Voucher != ''));
    VendTransButton.enabled(hasUserRights && FBTaxAssessmentPayment_BR.TaxAuthorityDataArea != '' && FBTaxAssessmentPayment_BR.Voucher != '');

    FBTaxAssessmentPayment_BR_ds.updateDesign();

    return ret;
}

private void initFromTaxAssessment(FBTaxAssessment_BR _taxAssessment)
{
    FBTaxAssessmentPayment_BR.initFromTaxAssessment(_taxAssessment);
}

public void initValue()
{
    super();

    FBTaxAssessmentPayment_BR.ICMSPaymentMonthRef   = taxAssessment.ToDate;

    this.initFromTaxAssessment(taxAssessment);
}

public display TaxAmount totalPayment(FBTaxAssessmentPayment_BR _taxAssessmentPayment)
{
    return (select sum(TaxAmounttoPay) from FBTaxAssessmentPayment_BR
                where FBTaxAssessmentPayment_BR.FBTaxAssessment_BR == _taxAssessmentPayment.FBTaxAssessment_BR
                    && FBTaxAssessmentPayment_BR.Reversed == false).TaxAmounttoPay;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    changeCompany(bookingPeriod.FiscalEstablishmentDataArea)
    {
        super(_formControl, _filterStr);
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    changeCompany(bookingPeriod.FiscalEstablishmentDataArea)
    {
        super(_formControl, _filterStr);
    }
}

void clicked()
{
    FBTaxAssessmentPaymentCreator_BR    creator;
    FBTaxAssessmentCalculator_BR        calculator;
    FBTaxAssessmentPayment_BR           newPayment;

    super();

    if (mustCreatePaymentByFiscalDocument)
    {
        ttsBegin;

        creator = FBTaxAssessmentPaymentCreator_BR::createFromFiscalDocuments(taxAssessment, DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
        newPayment = creator.run();

        ttsCommit;

        FBTaxAssessmentPayment_BR_ds.research(true);
    }
    else
    {
        calculator = FBTaxAssessmentCalculator_BR::construct(taxAssessment);
        calculator.calculate();

        if (calculator.getOpenPeriodicAssessmentAmountToPay() > 0)
        {
            ttsBegin;

            creator = FBTaxAssessmentPaymentCreator_BR::createFromPeriodicAssessment(taxAssessment, DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()), calculator);
            newPayment = creator.run();

            ttsCommit;

            FBTaxAssessmentPayment_BR_ds.research(true);
        }
        else
        {
            warning("@FBK4520028");
        }
    }
}

