void clicked()
{
    FBTaxAssessmentPayment_BR   taxAssessmentPayment;

    boolean canPost = true;

    super();

    if (FBTaxAssessmentPayment_BR.ContribAssessmentRegimen      != FBContribAssessmentRegimen_BR::None
        && FBTaxAssessmentPayment_BR.ContribAssessmentRegimen   != FBContribAssessmentRegimen_BR::Cumulative
        && FBTaxAssessmentPayment_BR.taxAssessment().ContribCreditAllocationMethod == FBContribCreditAllocationMethod_BR::Proportional)
    {
        canPost = Box::yesNo("@SYP4882705", DialogButton::No) == DialogButton::Yes;
    }

    if (canPost)
    {
        if (mustCreatePaymentByFiscalDocument)
        {
            ttsBegin;

            for (taxAssessmentPayment = FBTaxAssessmentPayment_BR_DS.getFirst(1);
            taxAssessmentPayment;
            taxAssessmentPayment = FBTaxAssessmentPayment_BR_DS.getNext())
            {
                FBTaxAssessmentPost_BR::postPayment(taxAssessmentPayment);
            }

            ttsCommit;
        }
        else
        {
            FBTaxAssessmentPost_BR::postPayment(FBTaxAssessmentPayment_BR);
        }

        FBTaxAssessmentPayment_BR_ds.research(true);
    }
}

void clicked()
{
    Args args;
    MenuFunction menuFunction;
    TaxTrans taxTrans;

    menuFunction = new menuFunction(menuitemDisplayStr(ledgerTransVoucher), MenuItemType::Display);

    changeCompany (FBTaxAssessmentPayment_BR.TaxAuthorityDataArea)
    {
        select firstOnly taxTrans
            where taxTrans.Voucher     == FBTaxAssessmentPayment_BR.Voucher
                && taxTrans.TransDate   == FBTaxAssessmentPayment_BR.TransDate;

        args = new args(this);
        args.record(taxTrans);
        menuFunction.run(args);
    }
}

