public boolean modified()
{
    boolean                     ret;

    TableId                     tableId = tableNum(FBTaxAssessmentPayment_BR);
    FieldId                     fieldId = fieldNum(FBTaxAssessmentPayment_BR, PaymentTermId);

    ret = super();

    if (FBTaxAssessmentPayment_BR.taxAssessment().TaxType == TaxType_BR::ICMSDiff &&
            mustCreatePaymentByFiscalDocument &&
            Box::yesNo(strFmt("@Brazil:FBTaxAssessmentPaymentModifiedField", fieldId2pname(tableId, fieldId)), DialogButton::No) == DialogButton::Yes)
    {
        FBTaxAssessmentPayment_BR.copyPaymentTermIdFromPayment();

        FBTaxAssessmentPayment_BR_DS.research();
    }

    return ret;
}

