public void addressButtonClicked(FormFunctionButtonControl _button)
{
    Args         args = new Args();
    ;

    if (module != FactureModule_RU::Empl)
    {
        args.caller(element);

        if (consignee.text())
        {
            args.record(CustTable::find(consignee.text()));
        }
        else
        {
            switch (module)
            {
                case FactureModule_RU::Cust                    :
                    args.record(CustTable::find(customer.text()));
                    break;
                case FactureModule_RU::TaxCorrectionSales      :
                case FactureModule_RU::TaxCorrectionPurch_Cust :
                    args.record(CustTable::find(factureCustomer.text()));
                    break;
                case FactureModule_RU::Vend                    :
                case FactureModule_RU::TaxCorrectionPurch      :
                case FactureModule_RU::TaxCorrectionSales_Vend :
                    args.record(CompanyInfo::find());
                    break;
            }
        }

        _button.menufunction().run(args);
    }
}

public display LogisticsAddressing addressDelivery()
{
    deliveryAddressObservableLink.observe();
    return LogisticsPostalAddress::findRecId(addressReferenceGroup.value()).Address;
}

public boolean canClose()
{
    boolean  ret;

    ret = super();

    if (! this.closedOk())
    {
        factureEngine.offRelease();
    }

    return ret;
}

public FormStringControl conteragentCtrl()
{
    if (module == FactureModule_RU::Cust)
    {
        return Seller;
    }
    else
    {
        return Buyer;
    }
}

void commFactureSelected(FactureJour_RU _factureJour)
{
    FormStringControl ctrlConteragent = this.conteragentCtrl();

    dateExternal_SameAs_Date.value(NoYes::No);
    dateExternal_SameAs_Date.modified();
    factureIdExternal.text(_factureJour.FactureExternalId);
    ctrlConteragent.text(_factureJour.CustVendInvoiceAccount);
    factureDate_External.dateValue(_factureJour.FactureDate_External);

    correct.value(_factureJour.CorrectionType != FactureCorrectionType_RU::Unknown);
    correct.modified();

    correctionType.selection(correct.value() ? _factureJour.CorrectionType : FactureCorrectionType_RU::Unknown);
    correctionType.modified();
}

boolean createFacture(FactureAction_RU _action)
{
    #define.controlName('ShowHistory')
    FormCheckBoxControl             showHistoryControl;
    NoYes                           update, preview;
    Printout                        printFacture;
    boolean                         ret;

    FactureJour_RU                  factureJour;
    FactureTrans_RU                 factureTrans;
    FactureTrans_RU                 factureTransOrig;
    FormObjectSet                   ds;
    RecId                           recId;
    Common                          tmpSource;
    Args                            args;
    FormRun                         formRun;
    FactureDelete_RU                factureDelete;
    FactureTaxCorrectionEngine_RU   factureTaxCorrectionEngine;
    boolean                         isTaxCorrDistribution = distributionType.visible()                                        &&
                                                            correctionType.selection()   != FactureCorrectionType_RU::Unknown &&
                                                            distributionType.selection() != TaxDistributionType_RU::None;
    Set                             setRecId              = new Set(Types::Int64);

    void cleanup()
    {
        ttsbegin;

        factureJour = FactureJour_RU::find(factureJour.FactureId, factureJour.Module, true);
        factureDelete = FactureDelete_RU::construct(factureJour);
        factureDelete.runOperation();

        ttscommit;
    }

    if (factureCreate.notEmpty())
    {
        switch (_action)
        {
            case FactureAction_RU::PostPrint :
                update       = NoYes::Yes;
                printFacture = isTaxCorrDistribution ?
                               Printout::After       :
                               Printout::Current;
                preview      = NoYes::No;
                break;
            case FactureAction_RU::PostOnly  :
                update       = NoYes::Yes;
                printFacture = Printout::After;
                preview      = NoYes::No;
                break;
            case FactureAction_RU::PrintOnly :
                update       = isTaxCorrDistribution ?
                               NoYes::Yes            :
                               NoYes::No;
                printFacture = isTaxCorrDistribution ?
                               Printout::After       :
                               Printout::Current;
                preview      = NoYes::No;
                ret          = false;
                break;
            case FactureAction_RU::Preview   :
                update       = NoYes::No;
                printFacture = Printout::Current;
                preview      = NoYes::Yes;
                ret          = false;
                break;
        }

        if (distributionType.visible())
        {
            if (distributionType.selection() == TaxDistributionType_RU::None &&
                correctionType.selection()   == FactureCorrectionType_RU::Revision)
            {
                error("@SYS4081839");
                return false;
            }
        }

        factureCreate.initParameters(update,
                                     preview,
                                     printFacture,
                                     printInMST.value(),
                                     factureEngine.getFactureExternalId(factureExternalId.text()),
                                     factureDate.dateValue(),
                                     element.custVendAC(),
                                     currency.text(),
                                     factureEngine.factureType(),
                                     consignee.text(),
                                     consignor.text(),
                                     factureEngine.offSessionID(),
                                     attorneyId.text(),
                                     attorneyIssuedName.text(),
                                     attorneyDate.dateValue(),
                                     addressReferenceGroup.value(),
                                     factureDate_External.dateValue(),
                                     factureExtIdNumSeqGroupId.text(),
                                     corrFactureExternalId,
                                     corrFactureDate,
                                     correctedPeriod.dateValue(),
                                     correctionType.selection(),
                                     factureCorrectionReference,
                                     factureReportDate.dateValue());

        factureCreate.parmVATOnPayment(vatOnPayment.value());
        factureCreate.parmDlvInfoNoPrint(deliveryNoPrint.value());
        factureCreate.parmCorrect(correct.value());
        factureCreate.parmCalledFromEditLines(true);
        factureCreate.parmInventProfileType(inventProfileType.selection());
        factureCreate.parmInventOwnerId(inventOwnerId.text());
        factureCreate.parmHeaderRevision(isHeaderRevision);
        factureCreate.parmProcessVAT(processVATControl.value());
        factureCreate.parmPurchBookVATProcessParametersRecId(purchBookVATProcessParametersReferenceGroup.value());
        factureCreate.parmCommPartner(factureEngine.commPartner());
        factureCreate.parmUseOriginalDocumentAsFacture(UseOriginalDocumentAsFacture.value());
        factureCreate.runOperation();

        if (factureCreate.getFactureJour())
        {
            ret = true;

            FactureCalcAmountAdjustments_RU::factureAdjUpdate(factureCreate.getFactureJour());
        }
    }
    else
    {
        ret = checkFailed("@SYS25907");
    }

    factureJour = factureCreate.getFactureJour();

    if (ret)
    {
        FactureJour_RU::handleOperationTypeCodes(factureJour);

        ds        = FormDataUtil::getFormDataSource(source);
        recId     = isHeaderRevision ? factureJour.RecId : source.RecId;
        tmpSource = new DictTable(source.TableId).makeRecord();

        if (ds)
        {
            ds.research();
        }

        select firstonly tmpSource
            where tmpSource.RecId == recId;

        if (ds)
        {
            ds.findRecord(tmpSource);
            ds.active();
        }

        if (callerForm)
        {
            showHistoryControl = callerForm.design().controlName(#controlName);
            if (showHistoryControl)
            {
                showHistoryControl.modified();
            }
        }
    }

    if (ret && distributionType.visible() && factureJour.CorrectedFactureExternalId)
    {
        try
        {
            factureJour = factureCreate.getFactureJour();

            switch (distributionType.selection())
            {
                case TaxDistributionType_RU::Manual :
                    args = new Args(formStr(FactureTaxCorrection_RU));
                    args.record(factureJour);

                    formRun = classfactory.formRunClass(args);
                    formRun.init();
                    formRun.run();
                    formRun.wait();

                    if (formRun.closedCancel())
                    {
                        cleanup();
                    }
                    break;

                case TaxDistributionType_RU::Auto :
                    factureTaxCorrectionEngine = FactureTaxCorrectionEngine_RU::newParameters(factureJour, false, false, true);

                    factureTransOrig.setTmp();
                    factureTransOrig.setTmpData(factureTaxCorrectionEngine.getFactureTransOrig());

                    factureTrans.setTmp();
                    factureTrans.setTmpData(factureTaxCorrectionEngine.getFactureTrans());

                    while select RecId from factureTransOrig
                    {
                        setRecId.add(factureTransOrig.RecId);
                    }

                    while select forupdate factureTrans
                    {
                        factureTaxCorrectionEngine.distributeAmount(factureTrans, factureTrans.vat, setRecId);
                    }

                    factureTaxCorrectionEngine.updateTrans();
                    break;
            }

            if (isTaxCorrDistribution                    &&
                (_action == FactureAction_RU::PostPrint  ||
                 _action == FactureAction_RU::PrintOnly) &&
                FactureJour_RU::find(factureJour.FactureId, factureJour.Module))
            {
                factureCreate.printJournal(true);
                if (_action == FactureAction_RU::PrintOnly)
                {
                    factureEngine.offSaveToCache(factureEngine.contragentSec(),
                                                 creditNote.value(),
                                                 inventProfileType.selection(),
                                                 inventOwnerId.text());
                    cleanup();
                    factureEngine.offRebuild();
                }
            }
        }
        catch(Exception::Error)
        {
            cleanup();

            return false;
        }
    }

    return ret;
}

CustVendAC custVendAC()
{
    str contragLoc;

    switch (module)
    {
        case FactureModule_RU::Cust :
            contragLoc  = customer.text();
            break;
        case FactureModule_RU::Vend :
            contragLoc  = vendor.text();
            break;
        case FactureModule_RU::Empl :
            contragLoc  = employee.text();
            break;
        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::TaxCorrectionSales      :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
        case FactureModule_RU::TaxCorrectionSales_Vend :
            contragLoc  = taxCorrectionContragentType.selection() == FactureTaxCorrectionContragentType_RU::Vendor ?
                          factureVendor.text()                                                                     :
                          factureCustomer.text();
            break;
    }

    return contragLoc;
}

