void enableEditCorrectedFactureExternalId_RU()
{
    if (correctionType.selection() == FactureCorrectionType_RU::Unknown)
    {
        correctedFactureExternalId.enabled(false);
        correctedFactureDate.enabled(false);
    }
    else
    {
        correctedFactureExternalId.enabled(true);
        correctedFactureDate.enabled(true);
    }

    CorrectedFactureGrid.visible(false);
    CorrectedFactureGroup.visible(true);
}

#define.parmCurrent('Current')
#define.parmRevision('Revision')
public void init()
{
    if (element.args().parmEnumType() != enumNum(FactureModule_RU))
        throw error("@SYS22996");

    source = element.args().record();

    if (element.args().parm() == #parmRevision)
    {
        callerForm = element.args().caller();
        module = source.(fieldNum(FactureJour_RU, Module));
        factureCreate = FactureJourCreate_RU::construct(module);
        factureEngine = FactureEditLinesEngineHeaderRev_RU::construct(factureCreate);
        factureEngine.insertJour(source);
        isHeaderRevision = true;
    }
    else
    {
        module = element.args().parmEnum();
        factureCreate = FactureJourCreate_RU::construct(module);
        factureEngine = FactureEditLinesEngine_RU::construct(factureCreate, module);
    }

    factureEngine.setForm(element);
    factureEngine.setSourceRecord(element.args().record());

    switch (element.args().parm())
    {
        case '' :
            break;
        case #parmCurrent :
            isCurrentJourOnly = true;
            break;
        case #parmRevision :
            factureEngine.setIsProforma(false);
            break;
        default :
            factureEngine.setIsProforma(true);
    }

    factureEngine.parmCurrentJourOnly(isCurrentJourOnly);

    deliveryAddressObservableLink = new FormObservableLink();

    super();

    filtersGroup.enabled(! isCurrentJourOnly);
    functionsButtonGroup.visible(! isHeaderRevision);

    factureEngine.setOffTransRecord(officialsTrans);
    factureEngine.setDatasources(custInvoiceJour_DS,
                                 custInvoiceJour_RU_DS,
                                 custInvoiceTrans_DS,
                                 custInvoiceTrans_RU_DS,
                                 vendInvoiceJour_DS,
                                 vendInvoiceTrans_DS,
                                 markupTrans_DS,
                                 emplAdvTable_DS,
                                 emplAdvLine_DS,
                                 ledgerJournalTable_DS,
                                 ledgerJournalTrans_DS,
                                 ledgerJournalTrans_Exists_DS,
                                 custCommReportTransItem_DS,
                                 vendCommReportTransItem_DS);

    factureEngine.setControlsTabs(custTab,
                                  vendTab,
                                  emplTab,
                                  ledgerJournalTab,
                                  headerAddressTab,
                                  headerOfficialsTab);
    factureEngine.setControlsContragents(customer,
                                         vendor,
                                         employee,
                                         factureCustomer,
                                         factureVendor,
                                         consignee,
                                         consignor);
    factureEngine.setTreeControls(custTree,
                                  vendTree,
                                  emplTree,
                                  ledgerJournalTree);
    factureEngine.setControlsOther(factureExternalId,
                                   factureExtIdNumSeqGroupId,
                                   currency,
                                   taxCorrectionContragentType,
                                   showJournals,
                                   creditNote,
                                   positive,
                                   dateExternal_SameAs_Date,
                                   factureExternalDateGroup,
                                   sourceGroup,
                                   custRoundOffGroup,
                                   custEndDiscGroup,
                                   vendRoundOffGroup,
                                   vendEndDiscGroup,
                                   factureDate,
                                   factureDate_External,
                                   correctionGroup,
                                   vatOnPayment,
                                   correctedFactureExternalId,
                                   correctedFactureDate,
                                   correctedPeriod,
                                   correct,
                                   inventProfileType,
                                   inventOwnerId,
                                   addressReferenceGroup,
                                   processVATControl,
                                   purchBookVATProcessParametersReferenceGroup,
                                   processVATGroup,
                                   distributionType,
                                   correctionType,
                                   commissionGroup,
                                   seller,
                                   buyer,
                                   factureIdExternal,
                                   module == FactureModule_RU::Cust ? custInvAmountGroup  : vendInvAmountGroup,
                                   module == FactureModule_RU::Cust ? custCommAmountGroup : vendCommAmountGroup,
                                   factureReportDate,
                                   useOriginalDocumentAsFacture);

    factureEngine.parmVendInvoiceGroupControl(vendInvoiceGroup);
    factureEngine.parmVendPaymentGroupControl(vendPaymentGroup);

    factureEngine.setControlsButtons(postPrintButton, postOnlyButton, printOnlyButton);

    factureEngine.dsInitInvoiceJour();
    factureEngine.dsInitAdvTable();
    factureEngine.dsInitAdvLine();
    factureEngine.dsInitLedgTable();
    factureEngine.dsInitLedgTrans();
    factureEngine.dsInitLedgTransExist();

    factureEngine.initCorrect();
    factureEngine.setEnabledButtons();
    factureEngine.setVisibleTabs();
    factureEngine.setVisibleContragents();
    factureEngine.contragentInit();
    factureEngine.contragentInitDefault();

    factureEngine.initFactureDate();
    factureEngine.initCurrency();
    factureEngine.initCreditNote();
    factureEngine.initPositive();
    factureEngine.initSourceGroup();

    factureEngine.initDeliveryAddress();

    factureEngine.initInventProfileType();
    factureEngine.lastValueInventProfileType(inventProfileType.selection());

    factureEngine.initInventOwnerId();
    factureEngine.lastValueInventOwnerId(inventOwnerId.text());

    factureEngine.refreshFactureExternalIdControl();
    factureEngine.initExternalDate();
    factureEngine.initVATOnPayment();
    factureEngine.initCorrectionGroup();
    factureEngine.initCorrectionType();
    factureEngine.initCommissionGroup();
    factureEngine.setVisibleControls();
    factureEngine.setEnableControls();

    factureEngine.initPurchBookVATProcessParametersRecId();
    factureEngine.initProcessVAT();

    factureEngine.offInit();

    factureButton.enabled(! distributionType.visible() && correctionType.selection() != FactureCorrectionType_RU::Revision);
}

void initCorrFactureDisplay()
{
    FactureJour_RU jour;
    FactureJour_RU jourRev;
    SalesPurch     salesPurch = SalesPurch::None;

    if (correctionType.selection() == FactureCorrectionType_RU::Unknown)
    {
        corrFactureExternalId = '';
        corrFactureDate = dateNull();
        lastRevisionNum = 0;
        lastRevisionDate = dateNull();
    }

    if (! corrFactureExternalId || ! corrFactureDate)
    {
        lastRevisionNum     = 0;
        lastRevisionDate    = dateNull();
        return;
    }

    jour = FactureJour_RU::findCorrectedFacture(factureCreate.module(),
                                                element.custVendAC(),
                                                corrFactureExternalId,
                                                corrFactureDate);

    if (correctionType.selection()  == FactureCorrectionType_RU::Revision &&
        factureEngine.factureType() == FactureType_RU::TaxCorrection      &&
        jour)
    {
        switch (element.args().parmEnum())
        {
            case FactureModule_RU::TaxCorrectionSales :
                salesPurch = SalesPurch::Sales;
                break;
            case FactureModule_RU::TaxCorrectionPurch :
                salesPurch = SalesPurch::Purch;
                break;
        }

        if (! conFind(FactureJour_RU::taxCorrectionModule(taxCorrectionContragentType.selection(), salesPurch), jour.Module))
        {
            jour = null;
        }
    }

    UseOriginalDocumentAsFacture.value(jour.UseOriginalDocumentAsFacture);

    [corrFactureExternalId, corrFactureDate] = jour ?
                                               FactureJour_RU::findLastFacture(jour, correctionType.selection()) :
                                               ['', dateNull()];

    if (correctionType.selection()  == FactureCorrectionType_RU::Correction &&
        jour                                                                &&
       (jour.FactureExternalId      != corrFactureExternalId                ||
        jour.FactureDate            != corrFactureDate))
    {
        warning(strFmt("@SYS4081878", corrFactureExternalId, corrFactureDate));
        jour = FactureJour_RU::findCorrectedFacture(factureCreate.module(),
                                                    element.custVendAC(),
                                                    corrFactureExternalId,
                                                    corrFactureDate);
    }

    if (jour)
    {
        select firstonly jourRev
            where (jourRev.RefRevisedFacture == jour.RecId  ||
                   jourRev.RecId             == jour.RecId) &&
                   jourRev.Revised           == NoYes::No;

        consignor.text(jourRev.Consignor ? jourRev.Consignor : consignor.text());
        consignee.text(jourRev.Consignee ? jourRev.Consignee : consignee.text());

        lastRevisionNum     = jourRev.RevisionNum;
        lastRevisionDate    = jourRev.CorrectionType == FactureCorrectionType_RU::Revision ?
                              jourRev.FactureDate                                          :
                              dateNull();
    }
    else
    {
        lastRevisionNum     = 0;
        lastRevisionDate    = dateNull();
    }
}

display FactureDate_RU lastRevisionDate()
{
    return lastRevisionDate;
}

display RevisionNum_RU lastRevisionNum()
{
    return lastRevisionNum;
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = 0)
{
    LogisticsLocationRecId ret = _postalAddressLocation;

    if (ret)
    {
        addressReferenceGroup.value(LogisticsPostalAddress::findByLocation(ret).RecId);
        addressReferenceGroup.modified();
    }
    else
    {
        if (! addressReferenceGroup.value())
        {
            factureEngine.initDeliveryAddress();
        }

        if (addressReferenceGroup.value())
        {
            ret = LogisticsPostalAddress::findRecId(addressReferenceGroup.value()).Location;
        }
        else
        {
            if (consignee.text())
            {
                ret = CustTable::find(consignee.text()).postalAddress().Location;
            }
            else
            {
                switch (module)
                {
                    case FactureModule_RU::Cust                    :
                        ret = CustTable::find(customer.text()).postalAddress().Location;
                        break;
                    case FactureModule_RU::TaxCorrectionSales      :
                    case FactureModule_RU::TaxCorrectionPurch_Cust :
                        ret = CustTable::find(factureCustomer.text()).postalAddress().Location;
                        break;
                    case FactureModule_RU::Vend                    :
                    case FactureModule_RU::TaxCorrectionPurch      :
                    case FactureModule_RU::TaxCorrectionSales_Vend :
                        ret = CompanyInfo::find().postalAddress().Location;
                        break;
                    case FactureModule_RU::Empl                    :
                         ret =   DirParty::primaryPostalAddress(
                                     HcmEmployment::find(EmployeeTable_RU::findByEmployeeId(employee.text()).HcmEmployment).Worker).Location;
                        break;
                }
            }
        }
    }

    return ret;
}

public void run()
{
    SysAnyType      sysAnyType;
    FactureJour_RU  factureJour;

    super();


    //the facture is redrawn from report for principal
    if (element.args().parmObject() && element.args().parmObject() is SysAnyType)
    {
        sysAnyType  = element.args().parmObject();
        factureJour = sysAnyType.value();

        this.commFactureSelected(factureJour);
        factureIdExternal.modified();
    }

    if (source.TableId == tableNum(SalesTable) && CustInvoiceJour)
    {
        if (!CustInvoiceJour_ds.includeInFacture(false, CustInvoiceJour, false))
        {
            CustInvoiceJour_ds.includeInFacture(true, CustInvoiceJour, true);
            factureEngine.refreshHeader();
        }
    }
}

void setCorrFacture()
{
    int64           recCount;
    FactureJour_RU  corrFacture;
    FactureJour_RU  tmpFactureJour;
    container       corrInvoiceList = factureEngine.jourList();
    boolean         hitYesOriginalDocumentAsFacture, hitNoOriginalDocumentAsFacture;

    if (! correct.value())
    {
        return;
    }

    if (correctionType.selection() == FactureCorrectionType_RU::Unknown)
    {
        correctionType.selection(FactureCorrectionType_RU::Correction);
    }

    corrFacture.setTmp();
    delete_from factureCorrectionReference;

    if (corrInvoiceList != connull())
    {
        tmpFactureJour = CustVendInvoiceJour::findLastCorrFacture_RU(corrInvoiceList, correctionType.selection());
        if (tmpFactureJour)
        {
            corrFacture.setTmpData(CustVendInvoiceJour::findLastCorrFacture_RU(corrInvoiceList, correctionType.selection()));
            while select * from corrFacture
            {
                factureCorrectionReference.CorrectedFactureExternalId = corrFacture.FactureExternalId;
                factureCorrectionReference.CorrectedFactureDate       = corrFacture.FactureDate;
                factureCorrectionReference.RefCorrectedFacture        = corrFacture.RefLastCorrFacture;
                if (corrFacture.UseOriginalDocumentAsFacture == NoYes::Yes)
                {
                    factureCorrectionReference.OriginalDocumentAsFacture = UnknownNoYes::Yes;
                    hitYesOriginalDocumentAsFacture = true;
                }
                else
                {
                    factureCorrectionReference.OriginalDocumentAsFacture = UnknownNoYes::No;
                    hitNoOriginalDocumentAsFacture = true;
                }
                factureCorrectionReference.insert();
            }
        }

        select count(RecId) from corrFacture;
        recCount = corrFacture.RecId;
    }

    CorrectedFactureGrid.visible(recCount > 1);
    CorrectedFactureGroup.visible(! CorrectedFactureGrid.visible());
    UseOriginalDocumentAsFacture.visible(factureEngine.useOriginalDocumentAsFactureCtrlVisible() && !CorrectedFactureGrid.visible());


    factureCorrectionReference_ds.research();
    factureCorrectionReference_ds.first();

    corrFactureExternalId   = factureCorrectionReference.CorrectedFactureExternalId;
    corrFactureDate         = factureCorrectionReference.CorrectedFactureDate;

    if (recCount <= 1)
    {
        UseOriginalDocumentAsFacture.value(factureCorrectionReference.OriginalDocumentAsFacture);
        element.initCorrFactureDisplay();
    }

    if (hitYesOriginalDocumentAsFacture && hitNoOriginalDocumentAsFacture)
    {
        warning("@SalesPurchBook:CombinedFactueShoudReferFactureWithTheSameValueOfOrig");
    }

    correctedFactureExternalId.allowEdit(! corrFactureExternalId);
    correctedFactureDate.allowEdit(correctedFactureExternalId.allowEdit());
}

/// <summary>
/// Sets correction facture and facture date.
/// </summary>
/// <param name = "_corrFactureExternalId">
/// Facture external number.
/// </param>
/// <param name = "_corrFactureDate">
/// Facture external date.
/// </param>
public void setCorrectionFacture(
    FactureExternalId_RU _corrFactureExternalId,
    FactureDate_RU _corrFactureDate)
{
    corrFactureExternalId = _corrFactureExternalId;
    corrFactureDate = _corrFactureDate;
    this.initCorrFactureDisplay();
}

