public void executeQuery()
{
    if (factureEngine.dsExecuteInvoiceJour(this))
    {
        super();

        if (isCurrentJourOnly)
        {
            factureEngine.clear();
            if (custInvoiceJour)
            {
                factureEngine.includeJour(true, custInvoiceJour, NoYes::Yes);
            }

            correct.value(custInvoiceJour_RU.Correct_RU);
            correctionType.selection(custInvoiceJour.correctionType_RU());
            factureEngine.initCorrectionGroup();
            factureEngine.setVisibleControls();

            factureEngine.initCreditNote();

            customer.text(custInvoiceJour.InvoiceAccount);
            vatOnPayment.value(custInvoiceJour_RU.vatOnPayment_RU);
            currency.text(custInvoiceJour.CurrencyCode);
        }

        element.setCorrFacture();
    }
}

