edit Qty includeAmount(boolean          _set,
                       CustInvoiceTrans _trans,
                       Qty              _qty)
{
    Qty  ret;

    if (_set)
    {
        ret = min(abs(_qty), abs(_trans.Qty - _trans.custInvoiceTrans_RU().FacturedQty_RU)) * (_trans.Qty >= 0 ? 1 : -1);
        factureEngine.updateTrans(_trans, ret);
    }
    else
    {
        ret = factureCreate.lookupTrans([ _trans.TableId, _trans.RecId, 0 ]);
    }

    factureEngine.refreshHeader();

    if (_set)
    {
        element.setCorrFacture();
    }

    return ret;
}

edit IncludeInFacture_RU includeInFacture(boolean             _set,
                                          CustInvoiceTrans    _trans,
                                          IncludeInFacture_RU _include)
{
    IncludeInFacture_RU ret = factureEngine.includeTrans(_set, _trans, _include);

    if (_set)
    {
        element.setCorrFacture();
        this.research(true);
    }
    return ret;
}

