}

public void modified()
{
    if (module == FactureModule_RU::Cust &&
        inventProfileType.selection() == InventProfileType_RU::CommissionPrincipalAgent)
    {
        officialsTrans.nameChanged(NoYes::No, NoYes::No);
    }
    else
    {
        officialsTrans.nameChanged(module == FactureModule_RU::Cust);
    }
    officialsTrans_ds.refresh();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    CustAccount                     cust;
    VendAccount                     vend;
    ;
    if (officialsTrans.isOurGuy() || module == FactureModule_RU::Empl)
    {
        if (module == FactureModule_RU::Cust &&
            factureEngine.inventOwnerId() &&
            inventProfileType.selection() == InventProfileType_RU::CommissionPrincipalAgent)
        {
            OfficialsTrans_RU::nameLookup(_formControl, false, "", officialsTrans.CustVendRelation);
        }
        else
        {
            OfficialsTrans_RU::nameLookup(_formControl,true);
        }
    }
    else
    {
        switch (module)
        {
            case FactureModule_RU::Cust                    :
                cust = customer.text();
                vend = "";
                break;
            case FactureModule_RU::Vend                    :
                cust = "";
                vend = vendor.text();
                break;
            case FactureModule_RU::TaxCorrectionPurch      :
            case FactureModule_RU::TaxCorrectionPurch_Cust :
            case FactureModule_RU::TaxCorrectionSales      :
            case FactureModule_RU::TaxCorrectionSales_Vend :
                if (taxCorrectionContragentType.selection() ==
                    FactureTaxCorrectionContragentType_RU::Vendor)
                {
                    cust = "";
                    vend = factureVendor.text();
                }
                else
                {
                    cust = factureCustomer.text();
                    vend = "";
                }
                break;
        }
        OfficialsTrans_RU::nameLookup(_formControl,
                                      false,
                                      cust,
                                      vend);
    }
}

