public boolean modified()
{
    // field value is changed before super()
    FactureTaxCorrectionContragentType_RU oldVal = this.selection() == FactureTaxCorrectionContragentType_RU::Customer ?
                                                   FactureTaxCorrectionContragentType_RU::Vendor                       :
                                                   FactureTaxCorrectionContragentType_RU::Customer;
    boolean ret;

    ret = super();

    if (ret)
    {
        factureEngine.offSaveToCache(oldVal == FactureTaxCorrectionContragentType_RU::Vendor ?
                                         factureVendor.text()                                :
                                         factureCustomer.text(),
                                         creditNote.value(),
                                         inventProfileType.selection());

        factureEngine.taxCorrectionContragentTypeChanged();
    }

    return ret;
}

