public boolean modified()
{
    boolean ret;
    // enum field value is changed before super()
    boolean oldVal = ! this.value();

    ret = super();
    if (ret)
    {
        factureEngine.offSaveToCache(module == FactureModule_RU::Cust ? customer.text() : vendor.text(),
                                     oldVal,
                                     inventProfileType.selection());

        factureEngine.criteriaChanged();
        factureEngine.offRebuild();
    }

    return ret;
}

