public boolean modified()
{
    boolean ret = super();

    if (ret && ! factureEngine.allowedInventProfileTypes().in(inventProfileType.selection()))
    {
        ret = checkFailed(strFmt("@GLS115697", new DictEnum(enumNum(InventProfileType_RU)).value2Label(inventProfileType.selection())));

        inventProfileType.selection(InventProfileType_RU::General);
    }

    if (inventProfileType.selection() == InventProfileType_RU::NotSpecified)
    {
        inventProfileType.selection(InventProfileType_RU::General);
    }

    factureEngine.offSaveToCache(module == FactureModule_RU::Cust ? customer.text() : vendor.text(),
                                    creditNote.value(),
                                    factureEngine.lastValueInventProfileType());

    factureEngine.criteriaChanged();
    factureEngine.offRebuild();

    factureEngine.lastValueInventProfileType(inventProfileType.selection());

    factureEngine.initCommissionGroup();
    factureEngine.setVisibleControls();

    return ret;
}

