public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    qbds;
    FactureJour_RU          factureJour;
    FormStringControl       ctrlConteragent = element.conteragentCtrl();

    qbds = query.addDataSource(tableNum(FactureJour_RU));

    findOrCreateRange_W(qbds, fieldNum(FactureJour_RU, Module), queryValue(module == FactureModule_RU::Cust ?
                                                                           FactureModule_RU::Vend           :
                                                                           FactureModule_RU::Cust));

    findOrCreateRange_W(qbds, fieldNum(FactureJour_RU, FactureType), queryValue(FactureType_RU::Invoice));
    findOrCreateRange_W(qbds, fieldNum(FactureJour_RU, InventProfileType), con2Str([InventProfileType_RU::CommissionAgent,
                                                                                    InventProfileType_RU::CommissionPrincipalAgent]));
    if (ctrlConteragent.text())
    {
        findOrCreateRange_W(qbds, fieldNum(FactureJour_RU, CustVendInvoiceAccount), ctrlConteragent.text());
    }

    findOrCreateRange_W(qbds, fieldNum(FactureJour_RU, FactureExternalId), this.valueStr());

    qbds = qbds.addDataSource(tableNum(CommReportTransItem_RU));
    qbds.addLink(fieldNum(FactureJour_RU, RecId), fieldNum(CommReportTransItem_RU, RefFactureJour));
    qbds.joinMode(JoinMode::ExistsJoin);

    findOrCreateRange_W(qbds, fieldNum(CommReportTransItem_RU, Approved), queryValue(NoYes::Yes));
    findOrCreateRange_W(qbds, module == FactureModule_RU::Cust              ?
                              fieldNum(CommReportTransItem_RU, CustAccount) :
                              fieldNum(CommReportTransItem_RU, VendAccount), queryValue(element.custVendAC()));

    qbds = qbds.addDataSource(tableNum(FactureTrans_RU));
    qbds.addLink(fieldNum(CommReportTransItem_RU, RecId), fieldNum(FactureTrans_RU, RefCommReportTrans));
    qbds.joinMode(JoinMode::NoExistsJoin);

    factureJour = FactureJour_RU::lookupFactureExternalId(this, query.pack());

    if (factureJour)
    {
        element.commFactureSelected(factureJour);
    }
}

