{
    boolean ret;

    ret = super();

    if (ret)
    {
        factureEngine.criteriaChanged();
        factureEngine.setVisibleControls();
    }

    return ret;
}

public Common lookupReference()
{
    SysReferenceTableLookup  lookup = SysReferenceTableLookup::newParameters(
        tableNum(PurchBookVATProcessParameters_RU),
        purchBookVATProcessParametersReferenceGroup,
        true);

    Query                               query;
    QueryBuildDataSource                qbds;
    PurchBookVATProcessParameters_RU    selectedRecord;

    lookup.addLookupfield(fieldNum(PurchBookVATProcessParameters_RU, OperationCode));

    query = new Query();
    qbds  = query.addDataSource(tableNum(PurchBookVATProcessParameters_RU));

    qbds.addRange(fieldNum(PurchBookVATProcessParameters_RU, ProcessingType)).value(queryValue(PurchBookProcessingType_RU::Standard));

    lookup.parmQuery(query);
    selectedRecord = lookup.performFormLookup();

    return selectedRecord;
}

