public boolean modified()
{
    boolean     ret;
    CustVendAC  oldAccount = this.text();

    ret = super();

    if (ret && this.visible())
    {
        factureEngine.offSaveToCache(oldAccount, creditNote.value(), inventProfileType.selection());

        if (! this.text())
        {
            this.text(factureEngine.contragentSec());
        }
        else
        {
            factureEngine.contragentSec(this.text());
        }

        factureEngine.contragentChanged(false);
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && this.visible())
    {
        if (this.text() && ! VendTable::exist(this.text()))
        {
            ret = checkFailed(strFmt("@GLS104927", this.text(), tableStr(VendTable), tablePName(VendTable)));
        }
    }

    return ret;
}

