public void lookup()
{
    NumberSequenceTable numberSequenceTable = NumberSequenceTable::findByNaturalKey(factureEngine.extFactureIdNumSeqCode());

    switch (module)
    {
        case FactureModule_RU::Cust :
            NumberSequenceList::lookupList_RU(this, numberSequenceTable);
            break;
        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::TaxCorrectionSales      :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
        case FactureModule_RU::TaxCorrectionSales_Vend :
            NumberSequenceList::lookupList_RU(this, numberSequenceTable);
            break;
        case FactureModule_RU::Empl :
//            NumberSequenceList::lookupList(this, factureEngine.extFactureIdNumSeqCode());
            break;
        default                     :
            throw error(Error::wrongUseOfFunction(funcName()));
    }
}

