public boolean modified()
{
    boolean ret;
    TransDate maxInvoiceDate;

    ret = super();

    if (ret)
    {
        if (factureReportDate.dateValue())
        {
            maxInvoiceDate = min(this.dateValue(), factureReportDate.dateValue());
        }
        else
        {
            maxInvoiceDate = this.dateValue();
        }

        factureEngine.maxInvoiceDate(maxInvoiceDate);

        factureEngine.initExternalDate();
        factureEngine.criteriaChanged(false);

        if (isHeaderRevision)
        {
            factureEngine.insertJour(source);
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        factureEngine.adjustExternalDate();
        factureEngine.criteriaChanged();            
    }

    return ret;
}

