{
    boolean ret = super();

    if (ret)
    {
        factureEngine.initExternalDate();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;
    TransDate maxInvoiceDate;

    ret = super();

    if (ret)
    {
        if (this.dateValue())
        {
            maxInvoiceDate = min(this.dateValue(), factureDate.dateValue());
        }
        else
        {
            maxInvoiceDate = factureDate.dateValue();
        }

        factureEngine.maxInvoiceDate(maxInvoiceDate);
        factureEngine.criteriaChanged(false);
    }

    return ret;
}

