public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        factureEngine.criteriaChanged();
        factureEngine.refreshFactureExternalIdControl();

        element.enableEditCorrectedFactureExternalId_RU();

        UseOriginalDocumentAsFacture.enabled(correctionType.selection() == FactureCorrectionType_RU::Unknown);

        element.initCorrFactureDisplay();

        factureButton.enabled((! distributionType.visible()                                  ||
                               distributionType.selection() == TaxDistributionType_RU::None) &&
                              correctionType.selection()    != FactureCorrectionType_RU::Revision);
    }

    return ret;
}

public void lookup()
{
    FactureJour_RU factureJour;
    SalesPurch     salesPurch = SalesPurch::None;

    if (correctionType.selection()  == FactureCorrectionType_RU::Revision &&
        factureEngine.factureType() == FactureType_RU::TaxCorrection)
    {
        switch (element.args().parmEnum())
        {
            case FactureModule_RU::TaxCorrectionSales :
                salesPurch = SalesPurch::Sales;
                break;
            case FactureModule_RU::TaxCorrectionPurch :
                salesPurch = SalesPurch::Purch;
                break;
        }
    }

    factureJour = FactureJour_RU::lookupCorrectedFactureExternalId(this,
                                                                   taxCorrectionContragentType.selection() == FactureTaxCorrectionContragentType_RU::Customer ?
                                                                   (factureCustomer.text() ? factureCustomer.text() : customer.text()) :
                                                                   (factureVendor.text()   ? factureVendor.text()   : vendor.text()),
                                                                   taxCorrectionContragentType.selection(),
                                                                   factureEngine.factureType() != FactureType_RU::TaxCorrection ?
                                                                   factureEngine.dateSet()                                      :
                                                                   new Set(Types::Int64),
                                                                   true,
                                                                   salesPurch);
    if (factureJour)
    {
        corrFactureDate = factureJour.FactureDate;
    }
}

