        factureEngine.criteriaChanged();
    }

    return ret;
}

public Common lookupReference()
{
    LogisticsLocationSelectionLookup lookup;

    switch (module)
    {
        case FactureModule_RU::Cust                    :
            lookup = LogisticsLocationSelectionLookup::newParameters(this, CustTable::find(customer.text()));
            break;
        case FactureModule_RU::TaxCorrectionSales      :
        case FactureModule_RU::TaxCorrectionPurch_Cust :
            lookup = LogisticsLocationSelectionLookup::newParameters(this, CustTable::find(factureCustomer.text()));
            break;
        case FactureModule_RU::Vend                    :
        case FactureModule_RU::TaxCorrectionPurch      :
        case FactureModule_RU::TaxCorrectionSales_Vend :
            lookup = LogisticsLocationSelectionLookup::newParameters(this, CompanyInfo::find());
            break;
        case FactureModule_RU::Empl                    :
            lookup = LogisticsLocationSelectionLookup::newParameters(this,
                HcmWorker::find(HcmEmployment::find(EmployeeTable_RU::findByEmployeeId(employee.text()).HcmEmployment).Worker));
            break;

        default                                        :
            throw error(Error::wrongUseOfFunction(funcName()));
    }
    lookup.addRoleByType(LogisticsLocationRoleType::Delivery);

    lookup.parmAllowAddAddress(false);
    lookup.parmReturnLocation (false);

    return lookup.performFormLookup();
}

