public void tabChanged(int _fromTab, int _toTab)
{
    if (_toTab == linesTab)
    {
        custInvoiceTrans_ds.research(true);
    }
    if (_toTab == markupTab)
    {
        markupTrans_DS.executeQuery();
    }
    if (_toTab == custVendAllLinesTab)
    {
        factureEngine.buildTree();
    }

    super(_fromTab, _toTab);
}

