void adjustMarkAll(boolean _mark)
{
    TmpFactureAmountAdjTrans_RU amountAdjTrans;

    amountAdjTrans.RecId = factureAdj.RecId;

    while select forupdate factureAdj
    {
        factureAdj.IncludeInBook = _mark;
        factureAdj.update();

        factureCalcAdj.updateBookInclusion(factureJour.FactureId, factureAdj);
    }

    factureAdj_DS.research();
    factureAdj_DS.positionToRecord(amountAdjTrans);
}

public void closeOk()
{
    if (operCodesUpdate)
    {
        operCodesUpdate.parmMarkedLinesSet(markedLinesSet.pack());
    }

    super();
}

void exportVATCancel(NoYes _cancelAll)
{
    FactureProcessingExport_RU          processingExport;
    SalesTmpExportFactureProcessing_RU  tmpProcessing;

    tmpProcessing.clear();
    tmpProcessing.initFromFactuerJour(factureJour);
    tmpProcessing.CurrentProcessingType = _cancelAll ? SalesBookFactureProcessingType_RU::None :
                                                       tmpProcessing.CurrentProcessingType;
    tmpProcessing.insert();

    processingExport = FactureProcessingExport_RU::construct(tmpProcessing, NoYes::Yes);
    processingExport.run();

    factureJour_DS.reread();
    factureJour_DS.refresh();
}

