public void init()
{
    xArgs     args = element.args();

    if (args.caller() && SysDictClass::isEqualOrSuperclass(classIdGet(args.caller()), classNum(FactureJourCreate_RU)))
    {
        jourCreate = args.caller();
    }

    if (args.caller() && args.caller() is FactureOperationCodesUpdate_RU)
    {
        operCodesUpdate = args.caller();
        markedLinesSet = Set::create(operCodesUpdate.parmMarkedLinesSet());
    }

    super();

    factureCalcAdj  = FactureCalcAmountAdjustments_RU::construct();
    factureJourParm = FactureJourParm_RU::newParameters(jourCreate ? jourCreate.getFactureJour() : null);
    headerData      = FactureHeaderData_RU::newFromCaller(factureJourParm);

    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceRU00023, funcName());

    if (operCodesUpdate)
    {
        FactureOperationCodesUpdate.visible(false);
        PrintMenuButton.visible(false);
        FactureDelete.visible(false);
        ExportVATButton.visible(false);
        FactureEditLinesHeaderRev.visible(false);
        FactureCorrectedAmounts.visible(false);
        MarkLine.visible(true);
        FactureOperationTypeCodes.visible(true);
        FactureJour_OperationTypeCodes.visible(true);
        MarkAllButton.visible(true);
        CommandButtonCancel.visible(true);
        CommandButtonOK.visible(true);
        HeaderGroup.visible(false);
    }
    else if (jourCreate)
    {
        factureJour.setTmp();
        factureTrans.setTmp();
        factureJour.setTmpData(jourCreate.getFactureJour());
        factureTrans.setTmpData(jourCreate.getFactureTrans());

        printMenuButton.visible(false);
        factureDelete.visible(false);

        moduleRange.value(SysQuery::value(jourCreate.module()));
    }
    else
    {
        if (args.parmEnumType() == enumNum(FactureModule_RU))
        {
            switch (args.parmEnum())
            {
                case FactureModule_RU::Cust :
                    moduleRange.value(SysQuery::value(FactureModule_RU::Cust)               +
                                      comma                                                +
                    SysQuery::value(FactureModule_RU::TaxCorrectionSales) +
                                      comma                                             +
                    SysQuery::value(FactureModule_RU::TaxCorrectionPurch_Cust));
                    exportVATButton.visible(true);
                    break;
                case FactureModule_RU::Vend :
                    moduleRange.value(SysQuery::value(FactureModule_RU::Vend)               +
                                      comma                                                +
                    SysQuery::value(FactureModule_RU::Empl)               +
                                      comma                                                +
                    SysQuery::value(FactureModule_RU::TaxCorrectionPurch) +
                                      comma                                                +
                    SysQuery::value(FactureModule_RU::TaxCorrectionSales_Vend));

                    factureReportDate.visible(true);
                    break;
                case FactureModule_RU::TaxCorrectionPurch :
                    moduleRange.value(SysQuery::value(FactureModule_RU::TaxCorrectionPurch) +
                                      comma                                                +
                    SysQuery::value(FactureModule_RU::TaxCorrectionPurch_Cust));
                    factureTypeBox.visible(false);
                    factureTypeBox.selection(FactureJour_RU::factureModule2factureType(args.parmEnum()));
                    break;
                case FactureModule_RU::TaxCorrectionSales :
                    moduleRange.value(SysQuery::value(FactureModule_RU::TaxCorrectionSales) +
                                      comma                                                +
                    SysQuery::value(FactureModule_RU::TaxCorrectionSales_Vend));
                    factureTypeBox.visible(false);
                    factureTypeBox.selection(FactureJour_RU::factureModule2factureType(args.parmEnum()));
                    break;
                default                     :
                    moduleRange.value(SysQuery::value(args.parmEnum()));
                    factureTypeBox.visible(false);
                    factureTypeBox.selection(FactureJour_RU::factureModule2factureType(args.parmEnum()));
            }
        }
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

