void initFromLedgerJournalTrans(LedgerJournalTrans _trans)
{
    QueryBuildRange      tmpRange;
    QueryBuildDataSource tmpDataSource;
    FactureTrans_RU      tmpTrans = FactureTrans_RU::findLedgerJournalTrans(_trans.JournalNum,
                                                                            _trans.LineNum,
                                                                            _trans.RecId,
                                                                            false);

    tmpDataSource = factureJour_DS.query().dataSourceTable(tableNum(FactureJour_RU)).addDataSource(tableNum(FactureTrans_RU));
    tmpDataSource.relations(true);
    tmpDataSource.joinMode(JoinMode::ExistsJoin);
    tmpRange = findOrCreateRange_W(tmpDataSource, fieldNum(FactureTrans_RU, RecId));
    tmpRange.value(SysQuery::value(tmpTrans.RecId));
}

