void initFromSortedList(RecordSortedList _list)
{
    FactureJour_RU   localFactureJour;
    str              range = '';
    FactureModule_RU module;

    while (_list.next(localFactureJour))
    {
        if (range)
        {
            range += comma;
        }
        else
        {
            module = localFactureJour.Module;
        }
        range += SysQuery::value(localFactureJour.RecId);
    }

    moduleRange.value(queryValue(module));
    factureRecIdRange.value(range ? range : SysQuery::valueEmptyString());
}

