        jourExport.visible(true);
    }
}

void transDetail()
{
    FactureTransDetail_RU   transDetail;

    int                     beginSel, endSel;
    str                     newText;

    transDetail = FactureTransDetail_RU::construct(factureTrans);
    if (transDetail.prompt())
    {
        transDetail.runOperation();

        [beginSel, endSel] = transNotesCtrl.getSelection();

        newText = transDetail.transTxt();

        if (beginSel)
        {
            newText = ' ' + newText;
        }

        transNotesCtrl.pasteText(newText);

        transNotesCtrl.setSelection(beginSel, beginSel + strLen(newText));
        transNotesCtrl.setFocus();
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }
        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;
        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void close()
{
    FormDataSource formDataSource;

    if (this.args() && (this.args().dataset() == tableNum(CustomJournalTable_RU) ||
    this.args().dataset() == tableNum(CustomCorrectionTable_RU)))
    {
        formDataSource = this.args().record().dataSource();
        formDataSource.reread();
        formDataSource.refresh();
        formDataSource.active();
    }

    super();
}

public edit boolean editMarkLine(
    boolean             _set,
    FactureJour_RU      _factureJour,
    boolean             _markLine
    )
{
    if (_set)
    {
        if (_markLine)
        {
            markedLinesSet.add(_factureJour.RecId);
        }
        else
        {
            markedLinesSet.remove(_factureJour.RecId);
        }
    }

    return markedLinesSet.in(_factureJour.RecId);
}

public display container displayOutdated(FactureJour_RU _factureJour)
{
    ImageReference image;
    container imageContainer;

    if (setOutdated.in(_factureJour.RecId))
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::Error);
        imageContainer = image.pack();
    }

    return imageContainer;
}

