    }

    return operationTypeCodes;
}

public int active()
{
    int ret;
    boolean isInvoice,
            isAdvanceReport,
            isReversedAmountDifference,
            isTaxCorrection,
            isPrepayment,
            isExport,
            recordFound,
            bookExists,
            isVendPrepayment,
            isFacture,
            isCustomGTD,
            isCustomCorrection,
            isCorrection;

    Object  tmpField;

    ret = super();

    isInvoice                  = factureJour.FactureType == FactureType_RU::Invoice;
    isAdvanceReport            = factureJour.FactureType == FactureType_RU::AdvanceReport;
    isReversedAmountDifference = (factureJour.FactureType == FactureType_RU::AmoutDifference    ||
                                  factureJour.FactureType == FactureType_RU::ExchAdj            ||
                                  factureJour.FactureType == FactureType_RU::AdvanceAdjustment  ||
                                  factureJour.FactureType == FactureType_RU::VATAdjustment)     &&
                                 factureJour.Reversed;
    isTaxCorrection            = factureJour.FactureType == FactureType_RU::TaxCorrection;
    isPrepayment               = factureJour.FactureType == FactureType_RU::Prepayment;
    isExport                   = factureJour.FactureType == FactureType_RU::Invoice && factureJour.isExportFacture();
    recordFound                = factureJour.RecId;
    bookExists                 = factureJour.bookProcessingExists();
    isVendPrepayment           = factureJour.FactureType == FactureType_RU::Prepayment &&
                                 factureJour.Module == FactureModule_RU::Vend;
    isFacture                  = factureJour.FactureType == FactureType_RU::Facture;
    isCustomGTD                = factureJour.FactureType == FactureType_RU::GTD;
    isCustomCorrection         = factureJour.FactureType == FactureType_RU::CustomCorrection;
    isCorrection               = factureJour.isCorrection();

    jourAdjustments.visible(isInvoice);

    factureCorrection.visible(isCorrection && ! factureJour.isExportFacture());

    boolean showJointCorrectionTab = isCorrection && factureJour.JointCorrection;
    Correction.visible(!showJointCorrectionTab);
    JointCorrection.visible(showJointCorrectionTab);

    if (factureJour.JointCorrection)
    {
        factureCorrectionReference_ds.executeQuery();
    }

    if (isCorrection)
    {
        factureOriginalReference_ds.initData();
    }
    jourOriginalFacture.visible(isCorrection);

    correctedFactureExternalId.visible(! factureJour.JointCorrection);
    correctedFactureDate.visible(! factureJour.JointCorrection);
    correctedFactureGrid.visible(factureJour.JointCorrection);

    officials.visible(isInvoice || isAdvanceReport || isTaxCorrection);

    factureTaxCorrHeader = null;
    jourCorrTotals.visible(factureJour.CorrectedFactureExternalId != '' && ! jourCreate);
    if (jourCorrTotals.visible() && jourCorrTotals.isActivePage())
    {
        factureCorrTotals_ds.initFactureCorrTotals();
    }

    factureTaxCorrLine = null;
    transCorrectedAmount.visible(factureJour.CorrectedFactureExternalId != '' && ! jourCreate);
    if (transCorrectedAmount.visible() && transCorrectedAmount.isActivePage())
    {
        factureCorrTrans_ds.initCorrectedLines();
    }

    transactVoucher.enabled(recordFound                      &&
                           ((! isInvoice                     &&
                             ! isAdvanceReport               &&
                             ! isReversedAmountDifference    &&
                             ! isExport                      &&
                             ! isFacture                     &&
                             ! isCustomGTD                   &&
                             ! isCustomCorrection            &&
                             ! isTaxCorrection)              ||
                            (factureJour.FactureType == FactureType_RU::Prepayment &&
                             factureJour.FactureDate == factureJour.FactureDate_External))
                           );
    custTrans.enabled(recordFound && ! isInvoice && ! isReversedAmountDifference && ! isExport && !isFacture);
    vendTrans.enabled(recordFound && ! isInvoice && ! isReversedAmountDifference);
    buttonInvent.enabled(isInvoice);
    factureDelete.enabled(recordFound           &&
                          ! factureJour.Revised &&
                          (isInvoice            ||
                          isAdvanceReport       ||
                          isTaxCorrection       ||
                          isPrepayment          ||
                          isExport              ||
                          isVendPrepayment      ||
                          isCustomGTD           ||
                          isCustomCorrection));

    factureSourceInvoiceJour.enabled(recordFound                                                    &&
                                     (isInvoice                                                     ||
                                      isCustomGTD                                                   ||
                                      isCustomCorrection                                            ||
                                      isAdvanceReport                                               ||
                                      isTaxCorrection                                               ||
                                      factureJour.FactureType == FactureType_RU::AmoutDifference    ||
                                      factureJour.FactureType == FactureType_RU::ExchAdj            ||
                                      factureJour.FactureType == FactureType_RU::AdvanceAdjustment  ||
                                      factureJour.FactureType == FactureType_RU::VATAdjustment)     &&
                                     ! isReversedAmountDifference);
    factureSourceInvoiceTrans.enabled(recordFound && (isInvoice || isAdvanceReport || isTaxCorrection));
    printMenuButton.enabled(recordFound && ! isReversedAmountDifference && ! isCustomGTD && ! isCustomCorrection);

    transInvoiceGroup.visible(isInvoice);
    transInvoiceLinesGroup.visible(isInvoice);
    transAdjustmentGroup.visible(factureJour.AmountAdjustment);

    custTrans.visible(factureJour.Module == FactureModule_RU::Cust);
    vendTrans.visible(factureJour.Module == FactureModule_RU::Vend && (! isCustomGTD && ! isCustomCorrection));

    tmpField = factureJour_DS.object(fieldNum(FactureJour_RU, FactureDate));
    tmpField.allowEdit(factureJour.AmountAdjustment                 &&
                       factureJour.Module == FactureModule_RU::Vend &&
                       ! bookExists                                 &&
                       ! isReversedAmountDifference);

    tmpField = factureJour_DS.object(fieldNum(FactureJour_RU, FactureDate_External));
    tmpField.allowEdit((isInvoice || isAdvanceReport) && ! bookExists && ! factureJour.Revised);

    tmpField = factureJour_DS.object(fieldNum(FactureJour_RU, CustVendInvoiceAccount));
    tmpField.allowEdit(isTaxCorrection && ! bookExists);

    tmpField = factureJour_DS.object(fieldNum(FactureJour_RU, FactureExternalId));
    tmpField.allowEdit(isTaxCorrection && ! bookExists);

    tmpField = factureJour_DS.object(fieldNum(FactureJour_RU, ExportDate));
    tmpField.allowEdit(! factureJour.ExportDate ||
                         factureJour.ProcessingType == SalesBookFactureProcessingType_RU::None);

    exportVATButton.enabled(isExport);
    isVATTaxAgent = factureJour.vatTaxAgent                                          ||
                   (isTaxCorrection                                                  &&
                   (factureJour.Module == FactureModule_RU::TaxCorrectionPurch       ||
                    factureJour.Module == FactureModule_RU::TaxCorrectionSales_Vend) &&
                    VendTable::find(factureJour.CustVendInvoiceAccount).vatTaxAgent_RU);

    factureEditLinesHeaderRev.enabled(! factureJour.isTmp() &&
                                      ! factureJour.Revised &&
                                       (factureJour.FactureType == FactureType_RU::TaxCorrection ||
                                        factureJour.FactureType == FactureType_RU::Invoice));

    factureCorrectedAmounts.visible(! factureJour.isTmp() && factureJour.CorrectedFactureExternalId != '');

    headerData = null;
    factureJourHeader_ds.initHeaderData();

    commReportJour.visible((isInvoice || isPrepayment) && factureJourHeader.AgencyAgreement == AgencyAgreement_RU::None);

    if (operCodesUpdate)
    {
        FactureJour_DS.object(fieldnum(FactureJour_RU, OperationTypeCodes)).allowEdit(!markedLinesSet.in(factureJour.RecId));
    }

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void displayOption(Common       _jour,
                          FormRowDisplayOption _options)
{
    FactureJour_RU jour = _jour;

    if (((jour.FactureType == FactureType_RU::AmoutDifference    ||
          jour.FactureType == FactureType_RU::ExchAdj            ||
          jour.FactureType == FactureType_RU::AdvanceAdjustment  ||
          jour.FactureType == FactureType_RU::VATAdjustment      ||
          jour.FactureType == FactureType_RU::TaxCorrection)     &&
          jour.Reversed)                                         ||
          jour.Revised)
    {
        _options.backColor(WinAPI::RGB2int(180, 180, 180));
    }
    super(_jour, _options);
}

public void executeQuery(boolean _showHistoryChanged = false)
{
    #localmacro.RefOriginalValue
        '((\%1.'            + fieldstr(FactureJour_RU, RefOriginalFacture)  +
        ' == \%2) || (\%1.' + fieldstr(FactureJour_RU, RecId)               +
        ' == \%2) || (\%1.' + fieldstr(FactureJour_RU, RefLastCorrFacture)  +
        ' == \%3))'
    #endmacro
    QueryBuildRange qbr;
    FactureJour_RU  jour;

    factureTypeRange.value(factureTypeBox.selection() == FactureType_RU::All ? "" : SysQuery::value(factureTypeBox.selection()));

    revisedRange.value(showRevised.value() || ! headerGroup.visible() ? SysQuery::valueUnlimited() : queryValue(NoYes::No));

    if (showHistory.value())
    {
        this.query().dataSourceTable(tableNum(FactureJour_RU)).clearDynalinks();
    }

    qbr = findOrCreateRange_W(this.query().dataSourceTable(tableNum(FactureJour_RU)),
                              fieldNum(FactureJour_RU, TableId),
                              showHistory.value()       ?
                              strFmt(#RefOriginalValue,
                                     this.query().dataSourceTable(tableNum(FactureJour_RU)).name(),
                                     factureJour.RefOriginalFacture ?
                                     factureJour.RefOriginalFacture :
                                     factureJour.RecId,
                                     factureJour.RefLastCorrFacture ?
                                     factureJour.RefLastCorrFacture :
                                     factureJour.RecId) :
                              SysQuery::valueUnlimited());

    qbr.status(RangeStatus::Hidden);

    jour.RecId = factureJour.RecId;

    super();

    if (_showHistoryChanged)
    {
        factureJour_ds.positionToRecord(jour);
    }

    if (! factureJour.RecId)
    {
        this.active();
    }
}

display FactureType_RU factureJourType(FactureJour_RU _factureJour)
{
    return _factureJour.FactureType;
}

public void init()
{
    xArgs                               args   = element.args();
    TmpPurchBookVATProcessLogTrans_RU   tmpPurchBookVATProcessLogTrans;
    TmpSalesBookVATProcessLogTrans_RU   tmpSalesBookVATProcessLogTrans;

    super();

    if (operCodesUpdate)
    {
        this.query(new Query(operCodesUpdate.queryRun().query().pack())).userUpdate(false);
        this.query().dataSourceTable(tablenum(FactureJour_RU)).clearDynalinks();
    }

    this.cacheAddMethod(tableMethodStr(FactureJour_RU, factureIdOriginal));
    this.cacheAddMethod(tableMethodStr(FactureJour_RU, factureRevNumOriginal));
    this.cacheAddMethod(tableMethodStr(FactureJour_RU, amount));
    this.cacheAddMethod(tableMethodStr(FactureJour_RU, taxAmount));
    this.cacheAddMethod(tableMethodStr(FactureJour_RU, amountInclTax));

    this.query().dataSourceTable(tableNum(FactureJour_RU)).addSortField(fieldNum(FactureJour_RU, FactureDate),     SortOrder::Descending);
    this.query().dataSourceTable(tableNum(FactureJour_RU)).addSortField(fieldNum(FactureJour_RU, CreatedDateTime), SortOrder::Descending);

    moduleRange       = this.query().dataSourceTable(tableNum(FactureJour_RU)).addRange(fieldNum(FactureJour_RU, Module));
    factureTypeRange  = this.query().dataSourceTable(tableNum(FactureJour_RU)).addRange(fieldNum(FactureJour_RU, FactureType));
    factureRecIdRange = this.query().dataSourceTable(tableNum(FactureJour_RU)).addRange(fieldNum(FactureJour_RU, RecId));
    revisedRange      = this.query().dataSourceTable(tableNum(FactureJour_RU)).addRange(fieldNum(FactureJour_RU, Revised));

    factureRecIdRange.status(RangeStatus::Hidden);
    moduleRange.status(RangeStatus::Hidden);
    factureTypeRange.status(RangeStatus::Hidden);
    revisedRange.status(RangeStatus::Hidden);

    if (args.record())
    {
        switch (args.dataset())
        {
            case tableNum(CustInvoiceTable) :
                args.record(args.record().custInvoiceJour());
            case tableNum(CustInvoiceJour)  :
            case tableNum(VendInvoiceJour)  :
                custVendInvoiceJour = args.record();
                element.initFromCustVendEmplJour(custVendInvoiceJour);
                factureTypeBox.selection(FactureType_RU::Invoice);
                break;

            case tableNum(EmplAdvTable_RU)  :
                custVendInvoiceJour = args.record();
                element.initFromCustVendEmplJour(custVendInvoiceJour);
                factureTypeBox.selection(FactureType_RU::AdvanceReport);
                break;

            case tableNum(SalesTable)       :
            case tableNum(PurchTable)       :
                salesPurchTable = args.record();
                element.initFromSalesPurchTable(salesPurchTable);
                factureTypeBox.selection(FactureType_RU::Invoice);
                break;

            case tableNum(TmpPurchBookVATProcessLogTrans_RU)    :
                tmpPurchBookVATProcessLogTrans = args.record();
                factureTypeBox.selection(tmpPurchBookVATProcessLogTrans.factureType());
                break;

            case tableNum(TmpSalesBookVATProcessLogTrans_RU)    :
                tmpSalesBookVATProcessLogTrans = args.record();
                factureTypeBox.selection(tmpSalesBookVATProcessLogTrans.factureType());
                break;

            case tableNum(LedgerJournalTrans)   :
                element.initFromLedgerJournalTrans(args.record());
                factureTypeBox.selection(FactureType_RU::TaxCorrection);
                break;

            case tableNum(CustomJournalTable_RU):
                element.initFromCustomJournalTable_RU(args.record());
                factureTypeBox.selection(FactureType_RU::GTD);
                break;

            case tableNum(CustomCorrectionTable_RU):
                element.initFromCustomCorrectionTable_RU(args.record());
                factureTypeBox.selection(FactureType_RU::CustomCorrection);
                break;

            case tableNum(FactureJour_RU) :
                factureTypeBox.selection(args.record().(fieldNum(FactureJour_RU, FactureType)));
                break;
        }

        if (factureTypeBox.selection() != FactureType_RU::All)
        {
            headerGroup.visible(false);
        }

        showRevised.value(NoYes::Yes);
    }
}

