}

            public void jumpRef()
            {
                const str controlName = 'ShowRevised';
                FormRun             formRun;
                FormCheckBoxControl showRevisedControl;
                Args                args = new Args();

                args.setupArgs(element.args().parm(),
                               element.args().parmEnumType(),
                               element.args().parmEnum());

                switch (factureJour.Module)
                {
                    case FactureModule_RU::TaxCorrectionSales       :
                    case FactureModule_RU::TaxCorrectionPurch_Cust  :
                        args.parmEnum(FactureModule_RU::Cust);
                        break;
                    case FactureModule_RU::TaxCorrectionPurch       :
                    case FactureModule_RU::TaxCorrectionSales_Vend  :
                        args.parmEnum(FactureModule_RU::Vend);
                        break;
                }

                MenuFunction mf = new MenuFunction(menuItemDisplayStr(FactureJournal_RU), MenuItemType::Display);
				formRun = mf.create(args);
                formRun.run();
                showRevisedControl = formRun.design().controlName(controlName);
                if (showRevisedControl)
                {
                    showRevisedControl.value(NoYes::Yes);
                    showRevisedControl.modified();
                }

                formRun.dataSource().positionToRecord(factureJour);
                formRun.detach();
            }

