public void executeQuery()
{
    RefRecId recId = factureTrans.InvoiceTransRecId;

    if (factureJour.CorrectionType == FactureCorrectionType_RU::Revision)
    {
        transRecIdRange.value(SysQuery::valueUnlimited());
        tmpTransIdRange.value(queryValue(recId));
    }
    else
    {
        transRecIdRange.value(queryValue(factureTaxCorrLine ? factureTaxCorrLine.getOrigTransRecId(recId) : 0));
        tmpTransIdRange.value(SysQuery::valueUnlimited());
    }

    super();
}

