    transRecIdRange.status(RangeStatus::Hidden);

    tmpTransIdRange = this.query().dataSourceTable(tableNum(FactureTrans_RU)).addRange(fieldNum(FactureTrans_RU, InvoiceTransRecId));
    tmpTransIdRange.status(RangeStatus::Hidden);
}

