public edit VendAccount editSeller(boolean _set, FactureJourHeader_RU _factureJourHeader, VendAccount _seller)
{
    VendTable            vendTable;
    FactureJourHeader_RU factureHeader;

    if (_set)
    {
        ttsbegin;

        factureHeader = factureJour.factureHeader(true);
        if (factureHeader)
        {
            vendTable = VendTable::find(_seller);
            if (vendTable)
            {
                factureHeader.Seller            = vendTable.AccountNum;
                factureHeader.CompanyName       = vendTable.name();
                factureHeader.CompanyAddress    = vendTable.postalAddress().Address;
                factureHeader.CompanyINN        = vendTable.partyINNasOfDate_RU(factureJour.FactureDate);
                factureHeader.CompanyKPP        = vendTable.partyKPPasOfDate_RU(factureJour.FactureDate);
                factureHeader.update();
            }
        }

        ttscommit;

        headerData = null;
        this.initHeaderData();
    }

    return factureJourHeader.Seller;
}

public void init()
{
    super();

    factureJourHeader.setTmp();

    this.query().dataSourceTable(tableNum(FactureJourHeader_RU)).clearDynalinks();
    this.query().dataSourceTable(tableNum(FactureJourHeader_RU)).clearRanges();
}

void initHeaderData()
{
    if (! headerData && (header.isActivePage() || officials.isActivePage()))
    {
        if (jourCreate)
        {
            headerData = FactureHeaderData_RU::newFromCaller(jourCreate);
        }
        else
        {
            factureJourParm.setFactureJour(factureJour);
            factureJourParm.setFactureTrans(factureTrans);
            headerData = FactureHeaderData_RU::newFromCaller(factureJourParm);
        }

        factureJourHeader.setTmpData(headerData.getHeaderTmp());
        this.executeQuery();

        commission_Seller.allowEdit(factureJour.FactureType != FactureType_RU::Prepayment && factureJourHeader.AgencyAgreement == AgencyAgreement_RU::PurchaseCommission);

        offReportTypeRange.value(queryValue(enum2int(headerData.reportType())));
        officialsTrans_ds.executeQuery();
    }
}

