public void executeQuery()
{
    QueryBuildRange qbr;

    qbr = findOrCreateRange_W(this.query().dataSourceTable(tablenum(FactureCorrectionReference_RU)),
                              fieldnum(FactureCorrectionReference_RU, FactureJour_RU),
                              queryValue(factureJour.RefRevisedFacture ?
                                         factureJour.RefRevisedFacture :
                                         factureJour.RecId));
    qbr.status(RangeStatus::Hidden);

    super();
}

public void init()
{
    super();

    factureOriginalReference.setTmp();
}

void initData()
{
    FactureJour_RU  origFacture;

    delete_from factureOriginalReference;

    if (factureJour)
    {
        origFacture = factureJour.getCorrectedFacture(true);
        while (origFacture)
        {
            factureOriginalReference.FactureExternalId  = origFacture.FactureExternalId;
            factureOriginalReference.FactureDate        = origFacture.FactureDate;
            factureOriginalReference.RefOriginalFacture = origFacture.RecId;
            factureOriginalReference.FactureJour_RU     = factureJour.RecId;
            factureOriginalReference.insert();

            next origFacture;
        }
    }

    this.research();
}

void clicked()
{
    FactureJour_RU  jour = FactureJour_RU::find(factureJour.FactureId, factureJour.Module),
                    save;

    factureJour_DS.next();
    if (factureJour.FactureId == jour.FactureId)
    {
        factureJour_DS.prev();
    }
    save = FactureJour_RU::find(factureJour.FactureId, factureJour.Module);
    this.find(jour);

    super();

    showHistory.modified();

    if (FactureJour_RU::find(jour.FactureId, jour.Module))
    {
        this.find(jour);
    }
    else
    {
        if (factureJour.isTmp())
        {
            factureJour.doDelete();
        }
        this.find(save);
    }
}

private void find(FactureJour_RU    _jour)
{
    factureJour_DS.research();
    if (factureJour.isTmp())
    {
        while (factureJour.FactureId != _jour.FactureId ||
               factureJour.Module    != _jour.Module)
        {
            if (! factureJour_DS.next())
                break;
        }
    }
    else
    {
        factureJour_DS.positionToRecord(_jour);
    }
}

void clicked()
{
    FactureCalcAmountAdjustments_RU factureCalcAmountAdj;
    FactureJour_RU                  factureJourLocal;
    Args                            args = new Args();

    for (factureJourLocal = getFirstSelection(factureJour_ds);
    factureJourLocal;
    factureJourLocal = factureJour_ds.getNext())
    {
        args.record(factureJourLocal);

        if (factureJourLocal.AmountAdjustment)
        {
            factureCalcAmountAdj = FactureCalcAmountAdjustments_RU::construct();
            factureCalcAmountAdj.printFacture(factureJourLocal, PrintCopyOriginal::Original);
        }
        else
        {
            new MenuFunction(menuitemOutputStr(FactureOriginal_RU), MenuItemType::Output).run(args);
        }
    }
}

