public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        if (showHistory.value())
        {
            setOutdated = Set::create(FactureJour_RU::outdatedFactures(factureJour.RefOriginalFacture ?
                                                                       factureJour.RefOriginalFacture :
                                                                       factureJour.RecId,
                                                                       factureJour.RefLastCorrFacture ?
                                                                       factureJour.RefLastCorrFacture :
                                                                       factureJour.RecId));
        }

        factureJourOutdated.visible(showHistory.value());
        revisionNum.visible(showHistory.value());
        factureJour_ds.object(fieldNum(FactureJour_RU, OrigRevisionNumDisplay)).visible(showHistory.value());

        factureJour_DS.executeQuery(true);
    }

    return ret;
}

