        if (dialog.run())
        {
            factureCalcAdj.calcFactureOnDate(factureJour, factureAdj.TransDate, dlgIncludedOnly.value());

            func = new MenuFunction(menuitemOutputStr(FactureOriginal_RU), MenuItemType::Output);
            args.caller(factureCalcAdj);
            args.record(factureCalcAdj.getFactureJour());
            func.run(args);
        }
    }
}

