Method.Source:
    [Form]
    public class FactureTaxCorrection_RU extends FormRun
    {
        FactureJour_RU                  factureJour;
        FactureTaxCorrectionEngine_RU   factureTaxCorrectionEngine;

        Set                             setOrigTrans;
        Map                             mapAmountsToDistribute;
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (this.closedOk())
    {
        ret = ret && factureTaxCorrectionEngine.updateTrans();
    }

    return ret;
}

public void init()
{
    if (! element.args().record() ||
          element.args().dataset() != tableNum(FactureJour_RU))
    {
        throw error(Error::missingFormActiveBuffer(element.form().name(), tableId2pname(tableNum(FactureJour_RU))));
    }

    factureJour = element.args().record();

    if (factureJour.RefRevisedFacture)
    {
        factureJour.SeqNumber--;
        factureTaxCorrectionEngine = FactureTaxCorrectionEngine_RU::newParameters(factureJour, false, false, true, true);
    }
    else
    {
        factureTaxCorrectionEngine = FactureTaxCorrectionEngine_RU::newParameters(factureJour, false, false, true);
    }

    super();

    factureTransCorr.setTmpData(factureTaxCorrectionEngine.getFactureTrans());
    factureTransOrig.setTmpData(factureTaxCorrectionEngine.getFactureTransOrig());

    setOrigTrans = new Set(Types::Int64);

    mapAmountsToDistribute = new Map(Types::Int64, Types::Real);
}

void setEnabledButtons()
{
    TaxAmount amount;

    if (mapAmountsToDistribute.exists(factureTransCorr.RecId))
    {
        amount = mapAmountsToDistribute.lookup(factureTransCorr.RecId);
    }
    else
    {
        amount = 0;
    }

    distribute.enabled(amount != 0);
}

public int active()
{
    int ret;

    ret = super();

    setOrigTrans = new Set(Types::Int64);
    element.setEnabledButtons();

    return ret;
}

public edit TaxAmount editDistributeAmount(boolean _set, FactureTrans_RU _factureTrans, TaxAmount _amount)
{
    TaxAmount amount;

    if (mapAmountsToDistribute.exists(_factureTrans.RecId))
    {
        amount = mapAmountsToDistribute.lookup(_factureTrans.RecId);
    }
    else
    {
        amount = 0;
    }

    if (_set)
    {
        amount = sign(_factureTrans.vat) * min(abs(_amount), abs(_factureTrans.vat));

        mapAmountsToDistribute.insert(_factureTrans.RecId, amount);
        element.setEnabledButtons();
    }

    return amount;
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(FactureJour_RU)).clearDynalinks();

    findOrCreateRange_W(this.query().dataSourceTable(tableNum(FactureJour_RU)),
                        fieldNum(FactureJour_RU, RecId),
                        con2Str(factureTaxCorrectionEngine.getOriginalFactures()));
}

private boolean allowOnlyTaxCorrection()
{
    boolean ret;

    const TransDate AllowOnlyTaxCorrectionDate = 01\01\2019;

    if (factureJour.FactureDate >= AllowOnlyTaxCorrectionDate)
    {
        FactureTrans_RU factureTransLocal;

        factureTransLocal.setTmp();
        factureTransLocal.setTmpData(factureTaxCorrectionEngine.getFactureTrans());

        while select factureTransLocal
        {
            LedgerJournalTrans_W ledgerJournalTrans_W = LedgerJournalTrans_RU_Extension::findFacture_RU(factureTransLocal).ledgerJournalTrans_W();

            if (ledgerJournalTrans_W.OrigPaymRefRecId_RU)
            {
                ret = true;
                break;
            }
        }
    }

    return ret;
}

public edit NoYes markNow(boolean _set, FactureTrans_RU _factureTrans, NoYes _mark)
{
    if (_set)
    {
        if (_mark)
        {
            setOrigTrans.add(_factureTrans.RecId);
        }
        else
        {
            setOrigTrans.remove(_factureTrans.RecId);
        }
    }

    return setOrigTrans.in(_factureTrans.RecId);
}

public void init()
{
    super();
    factureTransOrig.setTmp();

    this.query().dataSourceTable(tableNum(FactureTrans_RU)).clearDynalinks();

    if (!this.allowOnlyTaxCorrection())
    {
        this.query().dataSourceTable(tableNum(FactureTrans_RU)).addDynalink(fieldNum(FactureTrans_RU, TaxCode),  factureTransCorr, fieldNum(FactureTrans_RU, TaxCode));
        this.query().dataSourceTable(tableNum(FactureTrans_RU)).addDynalink(fieldNum(FactureTrans_RU, vatValue), factureTransCorr, fieldNum(FactureTrans_RU, vatValue));
    }
}

void clicked()
{
    FactureTrans_RU transCorr;
    TaxAmount       amount;

    if (mapAmountsToDistribute.exists(factureTransCorr.RecId))
    {
        amount = mapAmountsToDistribute.lookup(factureTransCorr.RecId);
    }
    else
    {
        amount = 0;
    }
    super();

    factureTaxCorrectionEngine.distributeAmount(factureTransCorr, amount, setOrigTrans);

    transCorr.RecId = factureTransCorr.RecId;
    factureTransCorr_ds.research();
    factureTransOrig_ds.research();

    factureTransCorr_ds.positionToRecord(transCorr);
}

void clicked()
{
    super();

    factureTaxCorrectionEngine.init();

    factureTransCorr_ds.research();
    factureTransOrig_ds.research();
}

void clicked()
{
    FactureTrans_RU transCorr;

    super();

    factureTaxCorrectionEngine.resetTaxCode(factureTransCorr.TaxCode);

    transCorr.RecId = factureTransCorr.RecId;
    factureTransCorr_ds.research();
    factureTransOrig_ds.research();

    factureTransCorr_ds.positionToRecord(transCorr);
}
