Method.Source:
    [Form]
    public class FiscalDocumentToComplementSelection_BR extends FormRun
    {
        Common newComplementaryInvoice;
        CustVendType_BR targetParty;
}

public boolean canClose()
{
    boolean ret;
    InvoiceComplementaryType_BR complementaryInvoiceType;

    ret = super();

    complementaryInvoiceType = str2enum(complementaryInvoiceType, complementaryType.valueStr());

    if (element.closedOk() &&
        (FiscalDocumentCustomer || FiscalDocumentVendor) &&
        (complementaryInvoiceType != InvoiceComplementaryType_BR::Price &&
        complementaryInvoiceType != InvoiceComplementaryType_BR::Tax))
    {
        ret = checkFailed("@GLS221014");
    }

    return ret;
}

public void closeOk()
{
    FiscalDocument_BR selectedFiscalDocument;
    ComplementaryInvoiceCreator creator;
    InvoiceComplementaryType_BR complementaryInvoiceType;

    super();

    complementaryInvoiceType = str2enum(complementaryInvoiceType, complementaryType.valueStr());

    if (complementaryInvoiceType != InvoiceComplementaryType_BR::Price &&
        complementaryInvoiceType != InvoiceComplementaryType_BR::Tax)
    {
        return;
    }

    if (targetParty == CustVendType_BR::Customer)
    {
        selectedFiscalDocument = getFirstSelection(FiscaldocumentCustomer_DS);
    }
    else if (targetParty == CustVendType_BR::Vendor)
    {
        selectedFiscalDocument = getFirstSelection(FiscaldocumentVendor_DS);
    }

    if (selectedFiscalDocument)
    {
        creator = ComplementaryInvoiceCreator::construct(selectedFiscalDocument, complementaryInvoiceType);
    }

    if (creator)
        newComplementaryInvoice = creator.run();
}

public void init()
{
    super();

    if (element.args().parmEnumType() == enumNum(CustVendType_BR))
    {
        targetParty = element.args().parmEnum();
        if (targetParty == CustVendType_BR::Customer)
        {
            VendorTab.visible(false);
            VendorTabLine.visible(false);

            FiscalDocumentCustomer_DS.query().dataSourceTable(tableNum(FiscalDocument_BR)).addSortField(fieldNum(FiscalDocument_BR, FiscalDocumentDate), SortOrder::Descending);
        }
        else
        {
            CustomerTab.visible(false);
            CustomerTabLine.visible(false);

            FiscalDocumentVendor_DS.query().dataSourceTable(tableNum(FiscalDocument_BR)).addSortField(fieldNum(FiscalDocument_BR, FiscalDocumentDate), SortOrder::Descending);
        }
    }
}
