private void applyFilters()
{
    switch (element.args().menuItemName())
    {
        case menuitemDisplayStr(FiscalDocumentListPageCust_BR):
            element.addFilterCust();
            break;

        case menuitemDisplayStr(FiscalDocumentListPageVend_BR):
            element.addFilterVend();
            break;

        case menuitemDisplayStr(FiscalDocumentListPageProj_BR):
            element.addFilterProj();
            break;

        case menuitemDisplayStr(FiscalDocumentLPCustCancelPending_BR):
            element.addFilterCust();
            element.addFilterPendingCancelation();
            break;

        case menuitemDisplayStr(FiscalDocumentLPVendCancelPending_BR):
            element.addFilterVend();
            element.addFilterPendingCancelation();
            break;

        case menuitemDisplayStr(FiscalDocumentLPCancelPending_BR):
            element.addFilterPendingCancelation();
            break;

        case menuitemDisplayStr(FiscalDocumentLPCustContigencyPending_BR):
            element.addFilterCust();
            element.addFilterPendingContigency();
            break;

        case menuitemDisplayStr(FiscalDocumentLPVendContigencyPending_BR):
            element.addFilterVend();
            element.addFilterPendingContigency();
            break;

        case menuitemDisplayStr(FiscalDocumentLPContigencyPending_BR):
            element.addFilterPendingContigency();
            break;

        case menuitemDisplayStr(FiscalDocumentListPageFiscalEstablish_BR):
            element.addFilterTransferOrder();
            break;

        case menuitemDisplayStr(FiscalDocumentListPageRetail_BR):
            element.addFilterRetail();
            break;
    }
}

private void addFilterCust()
{
    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, FiscalDocumentAccountType)).value(int2str(CustVendType_BR::Customer));

    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, Model)).value(SysQuery::valueNot(#FiscalDocModel_65));
}

private void addFilterPendingCancelation()
{
    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
        .addRange(fieldNum(FiscalDocument_BR, Status))
            .value(strFmt('((%1 == %2) || (%1 == %3))',
                          fieldStr(FiscalDocument_BR, Status),
                          int2str(FiscalDocumentStatus_BR::Rejected),
                          int2str(FiscalDocumentStatus_BR::RejectedNoFix)));
}

/// <summary>
/// Filters the fiscal documents that the user may switch to contigency mode if needed.
/// </summary>
/// <remarks>
/// Shows the fiscal documents that:
/// 1. Are electronic and were issued by a fiscal establishment that is in contigency mode;
/// 2. Were not sent to SEFAZ yet;
/// 3. Don't have its contigency mode set.
/// </remarks>
private void addFilterPendingContigency()
{
    const str NameParent = "ContingencyMode", NameChild = "SortActualMode";
    QueryBuildDataSource qbdsFiscalDocument, qbdsContingencyMode, qbdsSortActualMode;

    EFDocument_BR_ds.query().dataSourceTable(tableNum(EFDocument_BR)).joinMode(JoinMode::InnerJoin);

    EFDocument_BR_ds.query().dataSourceTable(tableNum(EFDocument_BR))
        .addRange(fieldNum(EFDocument_BR, MessageStatus))
            .value(int2str(EFDocMessageStatus_BR::Waiting));

    EFDocument_BR_ds.query().dataSourceTable(tableNum(EFDocument_BR))
        .addRange(fieldNum(EFDocument_BR, ContingencyMode))
            .value(int2str(EFDocContingMode_BR::Off));

    // <EFDocContingencyMode_BR> contains the contingency history for Fiscal Establishments
    // Adding secundary datasource to filter the actual Fiscal Establishment contingency mode
    qbdsFiscalDocument  = FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR));
    qbdsContingencyMode = qbdsFiscalDocument.addDataSource(TableNum(EFDocContingencyMode_BR), NameParent);
    qbdsSortActualMode = qbdsContingencyMode.addDataSource(TableNum(EFDocContingencyMode_BR), NameChild);
    qbdsContingencyMode.addLink(fieldNum(FiscalDocument_BR, FiscalEstablishment), fieldNum(EFDocContingencyMode_BR, FiscalEstablishment_BR));
    qbdsContingencyMode.addRange(fieldNum(EFDocContingencyMode_BR, Mode)).value(SysQuery::valueNot(int2str(EFDocContingMode_BR::Off)));
    qbdsSortActualMode.addRange(fieldNum(EFDocContingencyMode_BR, FiscalEstablishment_BR))
        .value(strFmt("(%1.%3 == %2.%3)", NameParent, NameChild, fieldStr(EFDocContingencyMode_BR, FiscalEstablishment_BR)));
    qbdsSortActualMode.addRange(fieldNum(EFDocContingencyMode_BR, CreatedDateTime))
        .value(strFmt("(%1.%3 < %2.%3)", NameParent, NameChild, fieldStr(EFDocContingencyMode_BR, CreatedDateTime)));
    qbdsSortActualMode.joinMode(JoinMode::NoExistsJoin);
}

private void addFilterProj()
{
    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, RefTableId))
                .value(strFmt('((%1 == %2) || (%1 == %3))',
                          fieldStr(FiscalDocument_BR, RefTableId),
                          int2str(tablenum(ProjInvoiceJour)),
                          int2str(tablenum(CustPackingSlipJour))));
}

private void addFilterTransferOrder()
{
    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, RefTableId)).value(queryValue(tablenum(InventTransferJour)));
}

private void addFilterVend()
{
    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, FiscalDocumentAccountType)).value(int2str(CustVendType_BR::Vendor));
}

private void addFilterRetail()
{
    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, Model)).value(#FiscalDocModel_65);

    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, Model)).value(#FiscalDocModel_02);

    FiscalDocument_BR_ds.query().dataSourceTable(tableNum(FiscalDocument_BR))
                .addRange(fieldNum(FiscalDocument_BR, Model)).value(#FiscalDocModel_59);
}

private str defineContingencyMessage()
{
    str contingencyMessage;

    if (EFDocContingencyMode_BR::getCurrentContingencyMode(FiscalDocument_BR.FiscalEstablishment).Mode == EFDocContingMode_BR::SecurityForm)
    {
        contingencyMessage = "@GLS62662";
    }
    else
    {
        contingencyMessage = "@GLS62661";
    }

    return contingencyMessage;
}

public str originalDocumentMenuItem()
{
    switch (FiscalDocument_BR.RefTableId)
    {
        case tableNum(CustInvoiceJour) :
            return menuitemDisplayStr(CustInvoiceJournal);

        case tableNum(VendInvoiceJour) :
            return menuitemDisplayStr(VendInvoiceJournal);

        case tableNum(SalesComplementaryInvoiceHeader) :
            return menuitemDisplayStr(SalesComplementaryInvoice);

        case tableNum(PurchComplementaryInvoiceHeader) :
            return menuitemDisplayStr(PurchComplementaryInvoice);

        case tableNum(CustPackingSlipJour):
            return menuitemDisplayStr(CustPackingSlipJournal);

        case tableNum(ProjInvoiceJour):
            return menuitemDisplayStr(ProjInvoiceJournal);

        case tableNum(InventTransferJour):
            return menuitemDisplayStr(InventTransferJour);

        case tableNum(TaxFiscalDocument_BR):
            return menuitemDisplayStr(TaxFiscalDocument_BR);
    }
    return '';
}

public void printInvoice(PrintCopyOriginal _action)
{
    FiscalDocument_BR               fiscalDocument;
    RecordSortedList                fiscalDocumentList = new RecordSortedList(tableNum(FiscalDocument_BR));
    FiscalDocumentPrintJob_BR       printJob = new FiscalDocumentPrintJob_BR();

    fiscalDocumentList.sortOrder(fieldNum(FiscalDocument_BR, RecId));

    for (fiscalDocument = getFirstSelection(FiscalDocument_BR_DS);
        fiscalDocument;
        fiscalDocument = FiscalDocument_BR_DS.getNext())
    {
        fiscalDocumentList.ins(fiscalDocument);
    }

    printJob.printFiscalDocuments(fiscalDocumentList, _action);
}

public void setTitleText()
{
    HeaderTitle.text(strFmt('%1 - %2, %3: %4 - %5', FiscalDocument_BR.FiscalEstablishment,
                                                    FiscalDocument_BR.FiscalDocumentSeries,
                                                    FiscalDocument_BR.FiscalDocumentNumber,
                                                    FiscalDocument_BR.FiscalDocumentAccountNum,
                                                    FiscalDocument_BR.ThirdPartyName));
}

public void updateAuditElements(FiscalDocument_BR _fiscalDocument)
{
    if (!_fiscalDocument.RecId)
        return;

    if (_fiscalDocument.AuditedBy)
    {
        if (auditMessageId != 0)
        {
            Message::Remove(auditMessageId);
        }

        auditMessageId = Message::Add(MessageSeverity::Informational, strFmt("@SYS4006993", _fiscalDocument.AuditedBy, _fiscalDocument.AuditedDateTime));
    }
    else
    {
        Message::Remove(auditMessageId);
        auditMessageId = 0;
    }
}

public void updateDesign()
{
    SecurityRights              curUserRights;
    FiscalDocumentLine_BR       lines;
    FiscalDocumentMiscCharge_BR charges;
    boolean existsVoucher;

    boolean originalDocumentUserRights;
    curUserRights = SecurityRights::newUser(curUserId());
    originalDocumentUserRights = curUserRights.hasMenuItemAccess(SecurableType::MenuItemDisplay, element.originalDocumentMenuItem());
    OriginalDocument.enabled(originalDocumentUserRights);

    this.updateAuditElements(FiscalDocument_BR);
    this.updateDesignForTextAndAdditionalInfo();

    existsVoucher = strLen(FiscalDocument_BR.Voucher) == 0 ? false : true;
    VoucherButton.enabled           (existsVoucher);
    TaxTransactions.enabled         (existsVoucher);   

    select firstOnly RecId from charges
        exists join lines
        where charges.FiscalDocumentLine    == lines.RecId
           && lines.FiscalDocument          == FiscalDocument_BR.RecId;

    MiscCharges.enabled             (charges.RecId == 0 ? false : true);

    this.updateDesignForServices();
    this.updateDesignForNfe();
    this.updateDesignForModel2();
    this.updateDesignForConsumerNfe();
    this.updateDesignForRetailConsumerDocument();

    NewComplementaryInvoice.enabled(FiscalDocument_BR.canBeComplemented());
    CCeGroup.enabled(FiscalDocument_BR.canBeCorrected());

    CancelReasonComment.visible(FiscalDocument_BR.shouldHaveCancelReasonComment());

    ImportDeclaration.enabled(FiscalDocument_BR.isImporting());
    ButtonUpdatePresumedTaxesFromNfe_BR.visible(BrazilParameters::find().ICMSPresumedTax == NoYes::Yes);

    allowEditFieldsOnFormDS_W(FiscalDocumentLine_BR_ds, false);

    if (ICMSSTAdditionalInfoFeature::isEnabled())
    {
        boolean isICMSSTAdditionalInfoEnabled = this.isICMSSTAdditionalInfoEnabled();

        FiscalDocumentLine_BR_ds.object(fieldNum(FiscalDocumentLine_BR, ICMSSTCollectionPaymentMode)).visible(isICMSSTAdditionalInfoEnabled);
        FiscalDocumentLine_BR_ds.object(fieldNum(FiscalDocumentLine_BR, ICMSSTCollectionPaymentNumber)).visible(isICMSSTAdditionalInfoEnabled);
        FiscalDocumentLine_BR_ds.object(fieldNum(FiscalDocumentLine_BR, RespWithholdingICMSST)).visible(isICMSSTAdditionalInfoEnabled);
        FiscalDocumentLine_BR_ds.object(fieldNum(FiscalDocumentLine_BR, NfeVbcstretVicmsstretTagsReported)).visible(isICMSSTAdditionalInfoEnabled);

        FiscalDocumentLine_BR_ds.object(fieldNum(FiscalDocumentLine_BR, NfeVbcstretVicmsstretTagsReported)).allowEdit(isICMSSTAdditionalInfoEnabled);
    }
}

