private void updateDesignForNfe()
{
    boolean isNfeEnabled = FiscalDocument_BR.isElectronic() || FiscalDocument_BR.isElectronicConsumerFiscalDoc();

    if (isNfeEnabled)
    {
        EFDocState_BR efDocState;

        if (FiscalDocument_BR.isElectronic())
        {
            ButtonVendInvoiceApprovalMatch_BR.enabled(efDocumentReceivedXml_BR.hasApprovedDivergences());
            if (FiscalDocument_BR.FiscalDocumentIssuer == FiscalDocumentIssuer_BR::OwnEstablishment)
            {
                ShowReceivedXmlGroup.visible(false);
                this.updateDesignForIssuedNFe(true);

                efDocState = EFDocState_BR::createFromEFdocument(EFDocument_BR);

                ResendButton.enabled(efDocState.canReSendMessage());
                ContigencyButton.enabled(efDocState.canChangeContingency());
                EmailButton.enabled(efDocState.canNotifyRecipients());
                EFDocXmlExport_BR.enabled(efDocState.canExportXML());
                EFDocPdfExport_BR.enabled(true);
                CCeGroup.enabled(FiscalDocument_BR.canBeCorrected());
            }
            else
            {
                ShowReceivedXmlGroup.visible(true);
                this.updateDesignForIssuedNFe(false);

                ViewReceivedDanfeButton.enabled(eFDocumentReceivedXML_BR.hasDanfe());
                ViewReceivedXmlMenuItemButton.enabled(eFDocumentReceivedXML_BR.hasAnyXml());
            }
        }
        else
        {
            ShowReceivedXmlGroup.visible(false);
            this.updateDesignForIssuedNFe(true);

            efDocState = EFDocState_BR::createFromEFdocument(EFDocument_BR);

            ResendButton.enabled        (efDocState.canReSendMessage());
            ContigencyButton.enabled    (efDocState.canChangeContingency());
            EmailButton.enabled         (efDocState.canNotifyRecipients());
            EFDocXmlExport_BR.enabled   (efDocState.canExportXML());
            EFDocPdfExport_BR.enabled   (true);
        }
    }
    else
    {
        ShowReceivedXmlGroup.visible(false);
        this.updateDesignForIssuedNFe(false);
    }

    ElectronicFiscalDocument.enabled(isNfeEnabled);
    this.visibleButtons();
}

private void updateDesignForIssuedNFe(boolean _visible)
{
    TabPageElectronicFD.visible(_visible);
    ActionsGroup.visible(_visible);
    EFDShowGroup.visible(_visible);
    CCeGroup.visible(_visible);
}

