}

display ContingencyDateTime getContingencyDateTime()
{
    ContingencyDateTime contingencyDateTime;
    if (EFDocument_BR.ContingencyMode != EFDocContingMode_BR::Off)
    {
        contingencyDateTime = DateTimeUtil::addSeconds(EFDocument_BR.ContingencyDateTime, EFDocument_BR.ContingencyDateTimeOffset);
    }
    return contingencyDateTime;
}

public void init()
{
    super();

    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    fiscalDocumentLine_BR_ds.object(fieldNum(FiscalDocumentLine_BR, ServiceTaxationTypeValue)).visible(BusinessDocumentParameters::isFeatureEnabled([EInvoicingFeaturesForService::BR00095]));

    FiscalDocumentTaxTrans_ds.object(fieldNum(FiscalDocumentTaxTrans_BR, TaxValue)).visible(false);
    FiscalDocumentTaxTrans_ds.object(fieldNum(FiscalDocumentTaxTrans_BR, DisplayTaxValue)).visible(true);
}

private void visibleButtons()
{
    boolean isFeatureEnabled = BusinessDocumentParameters::isFeatureEnabled([EInvoicingFeaturesForService::BR00053]);
    boolean isSubmissionlogExists = businessDocumentSubmission.RecId != 0;
    boolean isSubmissionRelatedlogExists = isSubmissionlogExists;

    if (isSubmissionlogExists && !businessDocumentSubmission.ExistExternal)
    {
        BusinessDocumentSubmission submission = BusinessDocumentSubmission::findStageNotZero(
            businessDocumentSubmission.RefTableId,
            businessDocumentSubmission.RefRecId);
        isSubmissionRelatedlogExists = submission.RecId != 0;
    }

    BusinessDocumentSubmissionDetails.visible(isSubmissionlogExists || isFeatureEnabled);
    BusinessDocumentSubmissionDetails.enabled(isSubmissionlogExists);
    BusinessDocumentSubmissionRelated.visible(isSubmissionRelatedlogExists || isFeatureEnabled);
    BusinessDocumentSubmissionRelated.enabled(isSubmissionRelatedlogExists);
    ViewXML.enabled(!isSubmissionlogExists);
    History.enabled(!isSubmissionlogExists);
}

private boolean isICMSSTAdditionalInfoEnabled()
{
    return conFind([tableNum(CustInvoiceJour), tableNum(VendInvoiceJour), tableNum(InventTransferJour)], FiscalDocument_BR.RefTableId)
        && FiscalDocument_BR.Direction == FiscalDocDirection_BR::Incoming;
}

