public void doAudit(boolean _audit)
{
    FiscalDocument_BR fiscalDocument;

    for (fiscalDocument = getFirstSelection(FiscalDocument_BR_DS);
    fiscalDocument;
    fiscalDocument = FiscalDocument_BR_DS.getNext())
    {
        FiscalDocument_BR::doAudit(_audit, fiscalDocument);
        FiscalDocument_BR_ds.reread();
        element.updateAuditElements(fiscalDocument);
    }
}

public void linkActive()
{
    QueryBuildDataSource fiscalDocumentDS = FiscalDocument_BR_ds.query().dataSourceNo(1);

    switch (element.args().dataset())
    {
        case tableNum(FiscalDocument_BR) :
            FiscalDocument_BR fiscalDocument = element.args().record();

            fiscalDocumentDS.clearDynalinks();
            fiscalDocumentDS.addRange(fieldNum(FiscalDocument_BR, RecId)).value(queryValue(fiscalDocument.RecId));
            break;

        case tableNum(FBSpedReinfEvent_BR) :
            FBSpedReinfEvent_BR spedReinfEvent = element.args().record();

            fiscalDocumentDS.clearDynalinks();

            QueryBuildDataSource qbdsEventFiscalDocument = fiscalDocumentDS.addDataSource(tableNum(FBSpedReinfEventFiscalDocument_BR));
            qbdsEventFiscalDocument.relations(true);
            qbdsEventFiscalDocument.joinMode(JoinMode::ExistsJoin);
            SysQuery::findOrCreateRange(qbdsEventFiscalDocument, fieldNum(FBSpedReinfEventFiscalDocument_BR, FBSpedReinfEvent_BR)).value(SysQuery::value(spedReinfEvent.RecId));
            break;
    }

    super();
}

public int active()
{
    int ret;

    ret = super();

    businessDocumentSubmission = BusinessDocumentSubmission::find(FiscalDocument_BR.TableId, FiscalDocument_BR.RecId);
    element.setTitleText();
    element.updateDesign();

    return ret;
}

public display ICMSDifRecipientTaxAmount icmsDifRecipientTaxTotalsAmount(FiscalDocumentTaxTrans_BR _taxTrans)
{
    FiscalDocumentTaxTransOutgoingICMSDif_BR fiscalDocTaxTransICMSDif;

    if (_taxTrans.Type == TaxType_BR::ICMSDiff)
    {
        select sum(ICMSDifRecipientTaxAmount) from fiscalDocTaxTransICMSDif
            where fiscalDocTaxTransICMSDif.FiscalDocumentLine == _taxTrans.FiscalDocumentLine;

        return  fiscalDocTaxTransICMSDif.ICMSDifRecipientTaxAmount;
    }
    else
    {
        return 0;
    }
}

public display ICMSDifIssuerTaxAmount icmsDifIssuerTaxTotalsAmount(FiscalDocumentTaxTrans_BR _taxTrans)
{
    FiscalDocumentTaxTransOutgoingICMSDif_BR fiscalDocTaxTransICMSDif;

    if (_taxTrans.Type == TaxType_BR::ICMSDiff)
    {
        select sum(ICMSDifIssuerTaxAmount) from fiscalDocTaxTransICMSDif
            where fiscalDocTaxTransICMSDif.FiscalDocumentLine == _taxTrans.FiscalDocumentLine;

        return  fiscalDocTaxTransICMSDif.ICMSDifIssuerTaxAmount;
    }
    else
    {
        return 0;
    }
}

public void executeQuery()
{            
    CfopDirectionCheck_BR cfopDirectionCheck = CfopDirectionCheck_BR::construct(FiscalDocumentLine_BR.CFOP);
    FiscalDocumentTaxTrans_BR fiscalDocTaxTrans;

    super();

    if (cfopDirectionCheck.isOtherState()
  &&  FiscalDocument_BR.FinalUser
  &&  !FiscalDocument_BR.ThirdPartyICMSContributor
  &&  FiscalDocument_BR.FiscalDocumentAccountType == CustVendType_BR::Customer
  &&  FiscalDocument_BR.FiscalDocumentIssuer == FiscalDocumentIssuer_BR::OwnEstablishment)
    {
        select firstOnly RecId from fiscalDocTaxTrans
            where fiscalDocTaxTrans.Type == TaxType_BR::ICMSDiff
            &&    fiscalDocTaxTrans.FiscalDocumentLine == FiscalDocumentLine_BR.RecId;

        if (fiscalDocTaxTrans.RecId)
        {
            FiscalDocumentTaxTrans_BR_ICMSDifIssuerTaxAmount.visible(true);
            FiscalDocumentTaxTrans_BR_ICMSDifRecipientTaxAmount.visible(true);                    
        }
    }
}

