public void clicked()
{
    FiscalDocument_BR fiscalDocument;
    boolean updated;
    boolean ret;

    super();

    fiscalDocument = FiscalDocument_BR_DS.getFirst(1);

    while (fiscalDocument.RecId != 0 && fiscalDocument.Direction == FiscalDocDirection_BR::Incoming)
    {
        ret = EFDocRecXmlUpdICMSST_BR::updateTaxesOfFiscalDocument(fiscalDocument);
        updated = ret ? ret : updated;

        fiscalDocument = FiscalDocument_BR_DS.getNext();
    }

    if (updated)
    {
        info("@NFE:ICMSSTUpdated");
    }
    else
    {
        info("@NFE:ICMSSTNotUpdated");
    }
}

